import React, { useEffect, useState } from "react";
import "./assets/styles.css";
import VLibras from "@djpfs/react-vlibras";

import {
  getBooks,
  getUsers,
  getLoans,
  getCurrentUser,
  login,
  logout,
  refreshToken
} from "./Api";

import LoanButton from "./components/LoanButton";
import UserAdminPanel from "./components/UserForm";
import { USER_ROLES } from "./constants/userRoles";

const Loader = () => <p>Carregando sistema...</p>;

function LoginForm({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setLoading(true);
    try {
      await login(username, password);
      await onLogin();
    } catch {
      setMessage("‚ùå Falha no login. Verifique usu√°rio e senha.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="login-form">
      <h3>üîê Acesso ao Sistema</h3>
      {message && <p className="form-message error">{message}</p>}
      <input
        placeholder="Usu√°rio"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        required
      />
      <input
        placeholder="Senha"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      <button type="submit" disabled={loading}>
        {loading ? "Entrando..." : "Entrar"}
      </button>
    </form>
  );
}

function BookList({ books, refreshBooks }) {
  return (
    <div className="admin-panel">
      <h2>üìö Lista de Livros</h2>
      {books.length === 0 ? (
        <p>Nenhum livro dispon√≠vel.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>T√≠tulo</th>
              <th>Autor</th>
              <th>A√ß√µes</th>
            </tr>
          </thead>
          <tbody>
            {books.map((book) => (
              <tr key={book.id}>
                <td>{book.id}</td>
                <td>{book.title}</td>
                <td>{book.author}</td>
                <td>
                  <LoanButton bookId={book.id} refreshBooks={refreshBooks} />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(USER_ROLES.GUEST);
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loadingUser, setLoadingUser] = useState(true);
  const [activeTab, setActiveTab] = useState("livros");

  const handleLogout = () => {
    logout();
    setUser(null);
    setRole(USER_ROLES.GUEST);
  };

  const fetchBooks = async () => {
    try {
      const data = await getBooks();
      setBooks(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao buscar livros:", err.message);
    }
  };

  const fetchUsers = async () => {
    if (![USER_ROLES.ADMIN, USER_ROLES.SUPERUSER].includes(role)) return;
    try {
      const data = await getUsers();
      setUsers(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao buscar usu√°rios:", err.message);
    }
  };

  const loadUserData = async () => {
    setLoadingUser(true);
    const token = localStorage.getItem("access_token");
    if (!token) {
      setRole(USER_ROLES.GUEST);
      setLoadingUser(false);
      return;
    }
    try {
      const userData = await getCurrentUser();
      if (!userData) throw new Error("Usu√°rio n√£o encontrado");

      setUser(userData);
      const newRole = userData.is_superuser
        ? USER_ROLES.SUPERUSER
        : userData.is_staff
        ? USER_ROLES.ADMIN
        : USER_ROLES.USER;
      setRole(newRole);

      await fetchBooks();
      if ([USER_ROLES.ADMIN, USER_ROLES.SUPERUSER].includes(newRole)) {
        await fetchUsers();
      }
    } catch (err) {
      console.error("Erro ao carregar usu√°rio:", err.message);
      handleLogout();
    } finally {
      setLoadingUser(false);
    }
  };

  useEffect(() => {
    loadUserData();
    const interval = setInterval(async () => {
      const newToken = await refreshToken();
      if (newToken) await loadUserData();
    }, 4 * 60 * 1000);
    return () => clearInterval(interval);
  }, []);

  if (loadingUser) return <Loader />;

  const isAdmin = [USER_ROLES.ADMIN, USER_ROLES.SUPERUSER].includes(role);

  return (
    <div className="app-container">
      <VLibras forceOnload />
      <header className="app-header">
        <h1>üìö Biblioteca Online Acess√≠vel</h1>
        {user ? (
          <div className="user-info">
            <p>
              Bem-vindo, <strong>{user.username}</strong> (
              {role === USER_ROLES.SUPERUSER
                ? "Superusu√°rio"
                : role === USER_ROLES.ADMIN
                ? "Administrador"
                : "Usu√°rio"}
              )
            </p>
            <button onClick={handleLogout}>Sair</button>
          </div>
        ) : (
          <LoginForm onLogin={loadUserData} />
        )}
      </header>

      {user && (
        <div className="tab-navigation">
          <button
            className={activeTab === "livros" ? "active-tab" : ""}
            onClick={() => setActiveTab("livros")}
          >
            üìö Livros
          </button>
          {isAdmin && (
            <button
              className={activeTab === "usuarios" ? "active-tab" : ""}
              onClick={() => setActiveTab("usuarios")}
            >
              üë§ Usu√°rios
            </button>
          )}
        </div>
      )}

      <main>
        {activeTab === "livros" && <BookList books={books} refreshBooks={fetchBooks} />}
        {activeTab === "usuarios" && isAdmin && (
          <UserAdminPanel users={users} fetchUsers={fetchUsers} />
        )}
      </main>

      <footer className="app-footer">
        <p>Desenvolvido com acessibilidade total ‚Äî suporte a Libras, teclado e alto contraste.</p>
      </footer>
    </div>
  );
}
