import React, { useState } from "react";
import axios from "axios";
import "../assets/styles.css";

// Fun√ß√£o para formatar mensagens de erro do backend
function formatErrorData(data) {
  if (!data) return null;
  if (typeof data === "string") return data;
  if (Array.isArray(data)) return data.join(" | ");
  if (typeof data === "object") {
    return Object.entries(data)
      .map(([k, v]) => (Array.isArray(v) ? `${k}: ${v.join(" ; ")}` : `${k}: ${v}`))
      .join(" ‚∏∫ ");
  }
  return String(data);
}

export default function UserForm({ role, refreshUsers }) {
  // üîπ Hooks sempre no topo
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ text: "", type: "" });

  // üîπ Usu√°rio n√£o-admin n√£o pode acessar
  if (role !== "admin") {
    return <p tabIndex="0">‚ö†Ô∏è Acesso negado. Apenas administradores podem criar usu√°rios.</p>;
  }

  // üîπ Envio do formul√°rio
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ text: "", type: "" });

    if (password !== password2) {
      setMessage({ text: "‚ö†Ô∏è As senhas n√£o coincidem.", type: "error" });
      setLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem("access_token");
      if (!token) {
        setMessage({
          text: "‚ö†Ô∏è Usu√°rio n√£o autenticado. Fa√ßa login como administrador.",
          type: "error",
        });
        setLoading(false);
        return;
      }

      const response = await axios.post(
        "http://127.0.0.1:8000/api/users/",
        { username, email, password },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessage({ text: "‚úÖ Usu√°rio criado com sucesso!", type: "success" });
      setUsername("");
      setEmail("");
      setPassword("");
      setPassword2("");

      if (refreshUsers) refreshUsers();
    } catch (err) {
      console.error("Erro ao criar usu√°rio:", err);
      let msg = "‚ùå Erro ao criar usu√°rio. Verifique os dados e tente novamente.";

      if (err.response) {
        const status = err.response.status;
        const detail = formatErrorData(err.response.data);

        if (status === 401) msg = "‚ö†Ô∏è N√£o autorizado (401). Token ausente ou inv√°lido.";
        else if (status === 403) msg = "‚ö†Ô∏è Permiss√£o negada (403). S√≥ admins podem criar usu√°rios.";
        else if (status >= 400 && status < 500) msg = detail || `Erro ${status}: requisi√ß√£o inv√°lida.`;
        else if (status >= 500) msg = `Erro do servidor (${status}). Verifique logs do backend.`;

        if (detail) msg += ` ‚Äî ${detail}`;
      } else if (err.request) {
        msg = "‚ö†Ô∏è Sem resposta do servidor. Verifique se o backend est√° rodando e CORS.";
      } else {
        msg = `Erro: ${err.message}`;
      }

      setMessage({ text: msg, type: "error" });
    } finally {
      setLoading(false);
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="user-form"
      aria-label="Formul√°rio de cadastro de usu√°rio"
      style={{
        display: "flex",
        flexDirection: "column",
        gap: "0.75rem",
        backgroundColor: "#fff",
        padding: "1.5rem",
        borderRadius: "10px",
        boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        maxWidth: "400px",
        margin: "1rem auto",
      }}
    >
      <fieldset disabled={loading} style={{ border: "none" }}>
        <legend
          tabIndex="0"
          style={{
            fontSize: "1.2rem",
            fontWeight: "bold",
            color: "#004999",
            textAlign: "center",
            marginBottom: "0.75rem",
          }}
        >
          üë§ Cadastro de Usu√°rio (Admin)
        </legend>

        <label htmlFor="username">Nome de Usu√°rio:</label>
        <input
          id="username"
          type="text"
          placeholder="Digite o nome de usu√°rio"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          aria-required="true"
        />

        <label htmlFor="email">E-mail:</label>
        <input
          id="email"
          type="email"
          placeholder="Digite o e-mail"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          aria-required="true"
        />

        <label htmlFor="password">Senha:</label>
        <input
          id="password"
          type="password"
          placeholder="Digite a senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          aria-required="true"
        />

        <label htmlFor="password2">Confirmar Senha:</label>
        <input
          id="password2"
          type="password"
          placeholder="Confirme a senha"
          value={password2}
          onChange={(e) => setPassword2(e.target.value)}
          required
          aria-required="true"
        />

        <button
          type="submit"
          disabled={loading}
          style={{
            background: loading ? "#999" : "#007bff",
            color: "#fff",
            padding: "0.6rem",
            border: "none",
            borderRadius: "6px",
            cursor: loading ? "not-allowed" : "pointer",
            transition: "background 0.3s",
          }}
        >
          {loading ? "Criando..." : "Criar Usu√°rio"}
        </button>
      </fieldset>

      {message.text && (
        <p
          role="status"
          className={`form-message ${message.type}`}
          tabIndex="0"
          style={{
            textAlign: "center",
            marginTop: "0.5rem",
            color: message.type === "success" ? "green" : "red",
            fontWeight: "bold",
          }}
        >
          {message.text}
        </p>
      )}
    </form>
  );
}
