// src/App.js
import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import VLibras from "@djpfs/react-vlibras";

import { getBooks, getUsers, getLoans, getCurrentUser } from "./services/api";

import BookList from "./components/BookList";
import BookForm from "./components/BookForm";
import UserForm from "./components/UserForm";
import LoanForm from "./components/LoanForm";
import LoginForm from "./components/LoginForm";
import AccessibilityBar from "./components/AccessibilityBar";

import "./App.css";

// Loader
const Loader = () => <p>Carregando sistema...</p>;

// Home p√∫blico / cat√°logo
const Home = ({ books, fetchBooks }) => (
  <section aria-label="Cat√°logo de livros">
    <h2 tabIndex="0">üìñ Cat√°logo de Livros</h2>
    <BookList books={books} refreshBooks={fetchBooks} />
  </section>
);

// Painel do administrador (admin e superuser)
const AdminPanel = ({ fetchBooks, fetchUsers, fetchLoans, users, books, role }) => (
  <section aria-label="√Årea de administra√ß√£o">
    <h2 tabIndex="0">‚öôÔ∏è Painel Administrativo</h2>

    {/* Link do Django Admin apenas para superusu√°rio */}
    {role === "superuser" && (
      <a
        href="http://127.0.0.1:8000/admin/"
        target="_blank"
        rel="noopener noreferrer"
        className="admin-link"
      >
        Abrir Django Admin
      </a>
    )}

    {/* üöÄ Topo: cadastros */}
    <div className="admin-top-panels">
      <div className="admin-panel">
        <h3 tabIndex="0">üìò Cadastrar Livro</h3>
        <BookForm onSave={fetchBooks} />
      </div>

      <div className="admin-panel">
        <h3 tabIndex="0">üë§ Cadastrar Usu√°rio</h3>
        <UserForm refreshUsers={fetchUsers} />
      </div>

      <div className="admin-panel">
        <h3 tabIndex="0">üìÑ Gerenciar Empr√©stimos</h3>
        <LoanForm onSave={fetchLoans} isAdmin={true} />
      </div>
    </div>

    {/* üìù Parte inferior: cat√°logo de livros */}
    <div className="admin-book-list">
      <h3 tabIndex="0">üìö Cat√°logo de Livros</h3>
      <BookList books={books} refreshBooks={fetchBooks} />
    </div>

    {/* üìù Lista de usu√°rios */}
    <div className="admin-user-list">
      <h3 tabIndex="0">Lista de Usu√°rios</h3>
      {users.length > 0 ? (
        <ul>
          {users.map((u) => (
            <li key={u.id}>
              {u.username} ({u.email}) {u.is_superuser && "- Superusu√°rio"}
            </li>
          ))}
        </ul>
      ) : (
        <p>Nenhum usu√°rio registrado</p>
      )}
    </div>
  </section>
);

export default function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null); // null = carregando
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loans, setLoans] = useState([]);

  const handleLogout = () => {
    localStorage.removeItem("access_token");
    localStorage.removeItem("refresh_token");
    setUser(null);
    setRole("guest");
  };

  const fetchBooks = async () => {
    try {
      const booksData = await getBooks();
      setBooks(Array.isArray(booksData) ? booksData : booksData.results || []);
    } catch (err) {
      console.error("Erro ao buscar livros:", err);
    }
  };

  const fetchUsers = async () => {
    if (role !== "admin" && role !== "superuser") return;
    try {
      const usersData = await getUsers();
      setUsers(usersData);
    } catch (err) {
      console.error("Erro ao buscar usu√°rios:", err);
    }
  };

  const fetchLoans = async () => {
    try {
      const loansData = await getLoans();
      setLoans(Array.isArray(loansData) ? loansData : loansData.results || []);
    } catch (err) {
      console.error("Erro ao buscar empr√©stimos:", err);
    }
  };

  useEffect(() => {
    const fetchUser = async () => {
      const token = localStorage.getItem("access_token");
      if (!token) {
        setRole("guest");
        return;
      }

      try {
        const userData = await getCurrentUser();
        setUser(userData);

        if (userData.is_superuser) setRole("superuser");
        else if (userData.is_staff) setRole("admin");
        else setRole("user");
      } catch (err) {
        console.error("Erro ao buscar usu√°rio:", err);
        handleLogout();
      }
    };

    fetchUser();
  }, []);

  useEffect(() => {
    if (user) {
      fetchBooks();
      fetchLoans();
      fetchUsers();
    }
  }, [user, role]);

  if (role === null) return <Loader />;

  return (
    <Router>
      <div className="app-container" aria-label="Sistema de Biblioteca Acess√≠vel">
        <VLibras forceOnload={true} />
        <AccessibilityBar />

        <header className="app-header" role="banner">
          <h1 tabIndex="0">üìö Biblioteca Online Acess√≠vel</h1>
          {user ? (
            <div className="user-info" role="navigation" aria-label="Sess√£o do usu√°rio logado">
              <p tabIndex="0">
                Bem-vindo, <strong>{user.username}</strong> (
                {role === "superuser"
                  ? "Superusu√°rio"
                  : role === "admin"
                  ? "Administrador"
                  : "Usu√°rio"}
                )
              </p>
              <button onClick={handleLogout} aria-label="Sair da conta">
                Sair
              </button>
            </div>
          ) : (
            <LoginForm
              setUser={setUser}
              setRole={setRole}
              onLoginSuccess={() => {
                fetchBooks();
                fetchLoans();
                fetchUsers();
              }}
            />
          )}
        </header>

        <main id="main-content" tabIndex="-1" role="main">
          <Routes>
            <Route path="/" element={<Home books={books} fetchBooks={fetchBooks} />} />
            <Route
              path="/admin"
              element={
                role === "admin" || role === "superuser" ? (
                  <AdminPanel
                    fetchBooks={fetchBooks}
                    fetchUsers={fetchUsers}
                    fetchLoans={fetchLoans}
                    users={users}
                    books={books}
                    role={role}
                  />
                ) : (
                  <Navigate to="/" />
                )
              }
            />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </main>

        <footer className="app-footer" role="contentinfo">
          <p tabIndex="0">
            Desenvolvido com acessibilidade total ‚Äî suporte a Libras, teclado e contraste.
          </p>
        </footer>
      </div>
    </Router>
  );
}
