import React, { useEffect, useState } from "react";
import BookGrid from "./BookGrid";
import { getBooks } from "../Api"; // ‚úÖ Usa fun√ß√£o centralizada
import "./booklist.css";

export default function BookList({ refreshBooksParent = null, isLoggedIn = false }) {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchBooks = async () => {
    if (!isLoggedIn) {
      setBooks([]);
      setError("üîí Fa√ßa login para acessar o cat√°logo de livros.");
      return;
    }

    setLoading(true);
    setError("");

    try {
      const list = await getBooks(); // ‚úÖ Usa API.js
      setBooks(list);
      if (refreshBooksParent) refreshBooksParent(list);
      console.log("üìö Livros carregados com sucesso:", list);
    } catch (err) {
      console.error("Erro ao buscar livros:", err);
      setError("‚ùå N√£o foi poss√≠vel carregar os livros. Verifique o servidor ou o token.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, [isLoggedIn]);

  const speakBook = (book) => {
    if (!book || !("speechSynthesis" in window)) return;
    const msg = new SpeechSynthesisUtterance(
      `Livro: ${book.title || "n√£o informado"}. Autor: ${book.author || "n√£o informado"}. Ano: ${book.publication_year || "n√£o informado"}. ${book.genre ? `G√™nero: ${book.genre}. ` : ""} Status: ${book.available ? "Dispon√≠vel" : "Emprestado"}.`
    );
    window.speechSynthesis.speak(msg);
  };

  return (
    <section className="book-list" aria-label="Cat√°logo de livros">
      {loading && <p tabIndex="0" aria-live="polite">‚è≥ Carregando livros...</p>}
      {!loading && error && <p tabIndex="0" aria-live="assertive" className="error-message">{error}</p>}
      {!loading && !error && books.length === 0 && isLoggedIn && <p tabIndex="0" aria-live="polite">üì≠ Nenhum livro encontrado no cat√°logo.</p>}
      {!loading && !error && books.length > 0 && (
        <BookGrid
          books={books.map((b) => ({
            ...b,
            speak: () => speakBook(b),
            altText: `Capa do livro ${b.title || "sem t√≠tulo"}`,
          }))}
          refreshBooks={fetchBooks}
        />
      )}
    </section>
  );
}
