import { getBooks, saveBook } from "../Api"; // âœ… corrigido
import "./bookform.css";

export default function BookForm({ onBookSaved }) {
  const [formData, setFormData] = useState({
    title: "",
    author: "",
    genre: "",
    publication_year: "",
  });
  const [books, setBooks] = useState([]);
  const [error, setError] = useState("");

  const loadBooks = async () => {
    try {
      const data = await getBooks();
      setBooks(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao carregar livros:", err);
    }
  };

  useEffect(() => {
    loadBooks();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await saveBook(formData);
      setFormData({
        title: "",
        author: "",
        genre: "",
        publication_year: "",
      });
      onBookSaved && onBookSaved();
      await loadBooks();
    } catch (err) {
      setError("Erro ao salvar o livro.");
    }
  };

