import React, { useEffect, useState } from "react";
import { api, authHeader } from "../Api";

export default function LoanForm({ onLoanCreated }) {
  const [books, setBooks] = useState([]);
  const [bookId, setBookId] = useState("");
  const [loanDate, setLoanDate] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // üîπ Carrega lista de livros da API
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await api.get("books/", { headers: authHeader() });
        setBooks(response.data);
      } catch (err) {
        console.error("Erro ao carregar livros:", err);
        setError("‚ö†Ô∏è N√£o foi poss√≠vel carregar a lista de livros.");
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!bookId) {
      setError("Selecione um livro para o empr√©stimo.");
      return;
    }

    try {
      await api.post(
        "loans/",
        {
          book_id: bookId,
          data_emprestimo: loanDate || new Date().toISOString().split("T")[0],
        },
        { headers: authHeader() }
      );

      setSuccess("‚úÖ Empr√©stimo registrado com sucesso!");
      setBookId("");
      setLoanDate("");

      // üîπ Atualiza lista no componente pai
      if (onLoanCreated) onLoanCreated();
    } catch (err) {
      console.error("Erro ao registrar empr√©stimo:", err.response?.data || err);
      setError("‚ùå Erro ao registrar empr√©stimo. Verifique os dados.");
    }
  };

  if (loading) return <p>‚è≥ Carregando livros...</p>;

  return (
    <section className="loan-form">
      <h2>üìö Registrar Empr√©stimo</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="book_id">Livro:</label>
        <select
          id="book_id"
          value={bookId}
          onChange={(e) => setBookId(e.target.value)}
          required
        >
          <option value="">Selecione um livro</option>
          {books.map((book) => (
            <option key={book.id} value={book.id}>
              {book.title}
            </option>
          ))}
        </select>

        <label htmlFor="loan_date">Data do Empr√©stimo:</label>
        <input
          type="date"
          id="loan_date"
          value={loanDate}
          onChange={(e) => setLoanDate(e.target.value)}
        />

        <button type="submit">Salvar Empr√©stimo</button>
      </form>

      {error && <p className="form-message error">{error}</p>}
      {success && <p className="form-message success">{success}</p>}
    </section>
  );
}
