import React, { useState, useEffect } from "react";
import BookForm from "./BookForm";
import LoanForm from "./LoanForm";
import UserForm from "./UserForm";
import BookList from "./BookList";
import LoanComponents from "./LoanComponents"; // <-- Agora listado
import "../assets/styles.css";

export default function AdminPanel({
  role,
  books = [],
  users = [],
  fetchBooks,
  fetchUsers,
  fetchLoans,
}) {
  const [refreshKey, setRefreshKey] = useState(0);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState("cadastro"); // <-- abas

  const handleRefresh = async () => {
    setLoading(true);
    await Promise.all([fetchBooks(), fetchUsers(), fetchLoans()]);
    setLoading(false);
    setRefreshKey((prev) => prev + 1);
  };

  useEffect(() => {
    handleRefresh();
    // eslint-disable-next-line
  }, []);

  return (
    <main className="admin-panel" role="main" aria-label="Painel Administrativo">
      <header className="admin-header">
        <h1 tabIndex="0">‚öôÔ∏è Painel de Administra√ß√£o</h1>

        {(role === "superuser" || role === "admin") && (
          <a
            href="http://127.0.0.1:8000/admin/"
            target="_blank"
            rel="noopener noreferrer"
            className="admin-link"
          >
            ‚öôÔ∏è Acessar Django Admin
          </a>
        )}
      </header>

      {/* Mensagem de loading */}
      {loading && (
        <p aria-live="polite" tabIndex="0">
          ‚è≥ Atualizando dados do servidor...
        </p>
      )}

      {/* üîπ Navega√ß√£o de abas */}
      <div className="admin-tabs">
        <button
          className={activeTab === "cadastro" ? "active" : ""}
          onClick={() => setActiveTab("cadastro")}
        >
          üìù Cadastros
        </button>

        <button
          className={activeTab === "livros" ? "active" : ""}
          onClick={() => setActiveTab("livros")}
        >
          üìö Livros
        </button>

        <button
          className={activeTab === "usuarios" ? "active" : ""}
          onClick={() => setActiveTab("usuarios")}
        >
          üë§ Usu√°rios
        </button>

        <button
          className={activeTab === "emprestimos" ? "active" : ""}
          onClick={() => setActiveTab("emprestimos")}
        >
          üîÑ Empr√©stimos
        </button>
      </div>

      <hr />

      {/* üîπ CONTE√öDO DAS ABAS */}
      <div className="admin-tab-content">
        {/* =======================
            ABA: CADASTROS
        ======================= */}
        {activeTab === "cadastro" && (
          <div className="admin-top-panels">
            <section className="admin-section" aria-label="Cadastro de livros">
              <h2 tabIndex="0">üìò Cadastrar Livros</h2>
              <BookForm onSave={handleRefresh} key={`book-${refreshKey}`} />
            </section>

            <section className="admin-section" aria-label="Cadastro de usu√°rios">
              <h2 tabIndex="0">üë§ Cadastrar Usu√°rios</h2>
              <UserForm onSave={handleRefresh} key={`user-${refreshKey}`} />
            </section>

            <section className="admin-section" aria-label="Gerenciar empr√©stimos">
              <h2 tabIndex="0">üìö Registrar Empr√©stimo</h2>
              <LoanForm
                onSave={handleRefresh}
                isAdmin={true}
                key={`loan-${refreshKey}`}
              />
            </section>
          </div>
        )}

        {/* =======================
            ABA: LIVROS
        ======================= */}
        {activeTab === "livros" && (
          <section aria-label="Lista de livros cadastrados">
            <h3 tabIndex="0">üìö Livros Registrados</h3>

            {books.length ? (
              <BookList
                books={books}
                refreshBooks={fetchBooks}
                key={`list-${refreshKey}`}
                isLoggedIn={true}
              />
            ) : (
              <p tabIndex="0">Nenhum livro cadastrado no momento.</p>
            )}
          </section>
        )}

        {/* =======================
            ABA: USU√ÅRIOS
        ======================= */}
        {activeTab === "usuarios" && (
          <section aria-label="Lista de usu√°rios cadastrados">
            <h3 tabIndex="0">üë• Usu√°rios Cadastrados</h3>

            {users.length ? (
              <ul className="user-list">
                {users.map((u) => (
                  <li key={u.id} tabIndex="0">
                    <strong>{u.username}</strong> ({u.email}) ‚Äî{" "}
                    {u.is_superuser
                      ? "‚≠ê Superusu√°rio"
                      : u.is_staff
                      ? "üëî Administrador"
                      : "üë§ Usu√°rio comum"}
                  </li>
                ))}
              </ul>
            ) : (
              <p tabIndex="0">Nenhum usu√°rio encontrado.</p>
            )}
          </section>
        )}

        {/* =======================
            ABA: EMPR√âSTIMOS
        ======================= */}
        {activeTab === "emprestimos" && (
          <section aria-label="Gerenciamento de empr√©stimos">
            <h3 tabIndex="0">üîÑ Todos os Empr√©stimos</h3>

            <LoanComponents role={role} user={null} />
          </section>
        )}
      </div>

      <footer className="admin-footer" tabIndex="0">
        <small>
          Sistema integrado √† API ‚Äî desenvolvido com acessibilidade e inclus√£o ‚ôø
        </small>
      </footer>
    </main>
  );
}
