import React, { useEffect, useState } from "react";
import api from "../services/api";
import Navbar from "../components/Navbar";
import BookCard from "../components/BookCard";

const AdminDashboard = () => {
  const [books, setBooks] = useState([]);
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [description, setDescription] = useState("");
  const [cover, setCover] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // Busca livros do backend
  const fetchBooks = async () => {
    try {
      setLoading(true);
      const res = await api.get("/books/");
      setBooks(Array.isArray(res.data) ? res.data : res.data.results);
    } catch (err) {
      console.error(err);
      setMessage("Erro ao buscar livros.");
    } finally {
      setLoading(false);
    }
  };

  // Adiciona um novo livro
  const handleAddBook = async () => {
    if (!title || !author) {
      setMessage("Título e Autor são obrigatórios.");
      return;
    }

    try {
      setLoading(true);
      const formData = new FormData();
      formData.append("title", title);
      formData.append("author", author);
      formData.append("description", description);
      if (cover) formData.append("cover_image", cover); // corresponde ao campo do Django

      await api.post("/books/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      // Reset dos campos
      setTitle(""); 
      setAuthor(""); 
      setDescription(""); 
      setCover(null);
      setMessage("Livro adicionado com sucesso!");
      fetchBooks();
    } catch (err) {
      console.error(err);
      setMessage("Erro ao adicionar livro.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div className="admin-dashboard">
      <Navbar />
      <h2 tabIndex="0">Dashboard do Administrador</h2>

      {message && <p tabIndex="0">{message}</p>}

      <section aria-label="Adicionar Livro">
        <h3 tabIndex="0">Adicionar Livro</h3>
        <input
          placeholder="Título"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          aria-label="Título do livro"
        />
        <input
          placeholder="Autor"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
          aria-label="Autor do livro"
        />
        <textarea
          placeholder="Descrição"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          aria-label="Descrição do livro"
        />
        <input
          type="file"
          onChange={(e) => setCover(e.target.files[0])}
          aria-label="Imagem de capa do livro"
        />
        <button onClick={handleAddBook} disabled={loading}>
          {loading ? "Salvando..." : "Adicionar"}
        </button>
      </section>

      <section aria-label="Acervo de Livros">
        <h3 tabIndex="0">Acervo</h3>
        {loading && <p tabIndex="0">Carregando livros...</p>}
        <div className="book-grid">
          {books.map((book) => (
            <BookCard key={book.id} book={book} refreshBooks={fetchBooks} />
          ))}
        </div>
      </section>
    </div>
  );
};

export default AdminDashboard;
