import React, { useState } from "react";
import "../assets/styles.css";
import { getAuthHeaders, refreshToken, logout } from "../services/api";

function formatErrorData(data) {
  if (!data) return null;
  if (typeof data === "string") return data;
  if (Array.isArray(data)) return data.join(" | ");
  if (typeof data === "object") {
    return Object.entries(data)
      .map(([k, v]) => (Array.isArray(v) ? `${k}: ${v.join(" ; ")}` : `${k}: ${v}`))
      .join(" ‚∏∫ ");
  }
  return String(data);
}

export default function UserForm({ role, refreshUsers }) {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ text: "", type: "" });

  // üîπ Somente administradores podem acessar
  if (role !== "admin") {
    return (
      <p tabIndex="0" role="alert">
        ‚ö†Ô∏è Acesso negado. Apenas administradores podem criar usu√°rios.
      </p>
    );
  }

  // üîπ Fun√ß√£o para envio do formul√°rio
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ text: "", type: "" });

    if (password !== password2) {
      setMessage({ text: "‚ö†Ô∏è As senhas n√£o coincidem.", type: "error" });
      setLoading(false);
      return;
    }

    try {
      const res = await fetch("http://127.0.0.1:8000/api/users/", {
        method: "POST",
        headers: {
          ...getAuthHeaders(),
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, email, password }),
      });

      // üîÑ Caso o token tenha expirado
      if (res.status === 401) {
        const newToken = await refreshToken();
        if (newToken) return handleSubmit(e);
        logout();
        alert("‚ö†Ô∏è Sess√£o expirada. Fa√ßa login novamente.");
        return;
      }

      const data = await res.json().catch(() => ({}));

      if (res.ok) {
        setMessage({ text: "‚úÖ Usu√°rio criado com sucesso!", type: "success" });
        setUsername("");
        setEmail("");
        setPassword("");
        setPassword2("");
        refreshUsers?.();
      } else {
        const msg = formatErrorData(data) || "Erro desconhecido ao criar usu√°rio.";
        setMessage({ text: `‚ùå ${msg}`, type: "error" });
      }
    } catch (err) {
      console.error("Erro ao criar usu√°rio:", err);
      setMessage({
        text:
          "‚ùå Erro de conex√£o. Verifique se o backend est√° em execu√ß√£o e se o CORS est√° habilitado.",
        type: "error",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="user-form"
      aria-label="Formul√°rio de cadastro de usu√°rio"
      style={{
        display: "flex",
        flexDirection: "column",
        gap: "0.75rem",
        backgroundColor: "#fff",
        padding: "1.5rem",
        borderRadius: "10px",
        boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        maxWidth: "400px",
        margin: "1rem auto",
      }}
    >
      <fieldset disabled={loading} style={{ border: "none" }}>
        <legend
          tabIndex="0"
          style={{
            fontSize: "1.2rem",
            fontWeight: "bold",
            color: "#004999",
            textAlign: "center",
            marginBottom: "0.75rem",
          }}
        >
          üë§ Cadastro de Usu√°rio (Admin)
        </legend>

        <label htmlFor="username">Nome de Usu√°rio:</label>
        <input
          id="username"
          type="text"
          placeholder="Digite o nome de usu√°rio"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          aria-required="true"
        />

        <label htmlFor="email">E-mail:</label>
        <input
          id="email"
          type="email"
          placeholder="Digite o e-mail"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          aria-required="true"
        />

        <label htmlFor="password">Senha:</label>
        <input
          id="password"
          type="password"
          placeholder="Digite a senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          aria-required="true"
        />

        <label htmlFor="password2">Confirmar Senha:</label>
        <input
          id="password2"
          type="password"
          placeholder="Confirme a senha"
          value={password2}
          onChange={(e) => setPassword2(e.target.value)}
          required
          aria-required="true"
        />

        <button
          type="submit"
          disabled={loading}
          style={{
            background: loading ? "#999" : "#007bff",
            color: "#fff",
            padding: "0.6rem",
            border: "none",
            borderRadius: "6px",
            cursor: loading ? "not-allowed" : "pointer",
            transition: "background 0.3s",
          }}
        >
          {loading ? "Criando..." : "Criar Usu√°rio"}
        </button>
      </fieldset>

      {message.text && (
        <p
          role="status"
          aria-live="polite"
          className={`form-message ${message.type}`}
          tabIndex="0"
          style={{
            textAlign: "center",
            marginTop: "0.5rem",
            color: message.type === "success" ? "green" : "red",
            fontWeight: "bold",
          }}
        >
          {message.text}
        </p>
      )}
    </form>
  );
}
