// src/components/LoanComponents.js
import React, { useState, useEffect } from "react";
import {
  getLoans,
  getUsers,
  getBooks,
  createLoan,
  returnBook,
} from "../Api";

export default function LoanComponents() {
  const [loans, setLoans] = useState([]);
  const [users, setUsers] = useState([]);
  const [books, setBooks] = useState([]);

  const [selectedUser, setSelectedUser] = useState("");
  const [typedUser, setTypedUser] = useState("");

  const [selectedBook, setSelectedBook] = useState("");
  const [typedBook, setTypedBook] = useState("");

  const [loading, setLoading] = useState(false);

  /** üîÑ Garantir que SEMPRE seja array */
  const normalizeArray = (data) => {
    if (Array.isArray(data)) return data;
    if (Array.isArray(data?.results)) return data.results;
    return [];
  };

  /** üîÑ Carrega tudo da API */
  const loadData = async () => {
    setLoading(true);
    try {
      const [loansData, usersData, booksData] = await Promise.all([
        getLoans(),
        getUsers(),
        getBooks(),
      ]);

      setLoans(normalizeArray(loansData));
      setUsers(normalizeArray(usersData));
      setBooks(normalizeArray(booksData));
    } catch (err) {
      console.error("Erro ao carregar dados:", err);
    }
    setLoading(false);
  };

  useEffect(() => {
    loadData();
  }, []);

  /** ‚ûï Registrar novo empr√©stimo */
  const handleCreateLoan = async () => {
    const userToSend = typedUser.trim() !== "" ? typedUser : selectedUser;
    const bookToSend = typedBook.trim() !== "" ? typedBook : selectedBook;

    if (!userToSend || !bookToSend) {
      alert("‚ö†Ô∏è Preencha o usu√°rio e o livro!");
      return;
    }

    const payload = {
      user_id: userToSend,
      book_id: bookToSend,
    };

    const ok = await createLoan(payload);

    if (ok) {
      alert("üìö Empr√©stimo registrado com sucesso!");
      loadData();
      setTypedUser("");
      setTypedBook("");
      setSelectedUser("");
      setSelectedBook("");
    }
  };

  /** üîÑ Registrar devolu√ß√£o */
  const handleReturnLoan = async (loanId) => {
    const ok = await returnBook(loanId);
    if (ok) {
      alert("üì¶ Livro devolvido com sucesso!");
      loadData();
    }
  };

  return (
    <div className="loan-panel">
      <h2>üìö Gerenciar Empr√©stimos</h2>

      {loading && <p>‚è≥ Carregando...</p>}

      {/* ============================
          üìå FORMUL√ÅRIO DE EMPR√âSTIMO
      ============================= */}
      <div className="loan-form">
        <h3>‚ûï Registrar Empr√©stimo</h3>

        {/* Selecionar OU digitar usu√°rio */}
        <label>Usu√°rio:</label>
        <select
          value={selectedUser}
          onChange={(e) => setSelectedUser(e.target.value)}
        >
          <option value="">Selecione um usu√°rio...</option>
          {users.map((u) => (
            <option key={u.id} value={u.id}>
              {u.username}
            </option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Ou digite o ID do usu√°rio"
          value={typedUser}
          onChange={(e) => setTypedUser(e.target.value)}
        />

        {/* Selecionar OU digitar livro */}
        <label>Livro:</label>
        <select
          value={selectedBook}
          onChange={(e) => setSelectedBook(e.target.value)}
        >
          <option value="">Selecione um livro...</option>
          {books
            .filter((b) => b.available)
            .map((b) => (
              <option key={b.id} value={b.id}>
                {b.title}
              </option>
            ))}
        </select>

        <input
          type="text"
          placeholder="Ou digite o ID do livro"
          value={typedBook}
          onChange={(e) => setTypedBook(e.target.value)}
        />

        <button onClick={handleCreateLoan} className="loan-btn">
          ‚úî Registrar Empr√©stimo
        </button>
      </div>

      {/* ============================
          üìå LISTA DE EMPR√âSTIMOS
      ============================= */}
      <h3 style={{ marginTop: "2rem" }}>üìÑ Empr√©stimos Registrados</h3>

      {loans.length === 0 ? (
        <p>Nenhum empr√©stimo registrado.</p>
      ) : (
        <ul className="loan-list">
          {loans.map((loan) => (
            <li key={loan.id} className="loan-item">
              <strong>Livro:</strong> {loan.book_title || loan.book?.title} <br />
              <strong>Usu√°rio:</strong> {loan.username || loan.user_username} <br />
              <strong>Retorno:</strong>{" "}
              {loan.return_date ? loan.return_date : "Ainda n√£o devolvido"}
              <br />
              {!loan.return_date && (
                <button
                  onClick={() => handleReturnLoan(loan.id)}
                  className="return-btn"
                >
                  üîÑ Registrar Devolu√ß√£o
                </button>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
