// src/App.js
import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from "react-router-dom";
import VLibras from "@djpfs/react-vlibras";

import {
  getBooks,
  getUsers,
  getLoans,
  getCurrentUser,
  login,
  logout,
  saveBook,
  deleteBook,
} from "./Api";

import BookList from "./components/BookList";
import BookForm from "./components/BookForm";
import UserForm from "./components/UserForm";
import LoanForm from "./components/LoanForm";
import LoginForm from "./components/LoginForm";
import AccessibilityBar from "./components/AccessibilityBar";

import "./App.css";

// Loader
const Loader = () => <p>Carregando sistema...</p>;

// Home p√∫blico / cat√°logo
const Home = ({ books, refreshBooks }) => (
  <section aria-label="Cat√°logo de livros">
    <h2 tabIndex="0">üìñ Cat√°logo de Livros</h2>
    <BookList books={books} refreshBooks={refreshBooks} />
  </section>
);

// Painel do administrador
const AdminPanel = ({ books, users, fetchBooks, fetchUsers, fetchLoans, role }) => (
  <section aria-label="√Årea de administra√ß√£o">
    <h2 tabIndex="0">‚öôÔ∏è Painel Administrativo</h2>

    {role === "superuser" && (
      <a
        href="http://127.0.0.1:8000/admin/"
        target="_blank"
        rel="noopener noreferrer"
        className="admin-link"
      >
        Abrir Django Admin
      </a>
    )}

    <div className="admin-top-panels">
      <div className="admin-panel">
        <h3 tabIndex="0">üìò Cadastrar / Editar Livro</h3>
        <BookForm onSave={fetchBooks} />
      </div>

      <div className="admin-panel">
        <h3 tabIndex="0">üë§ Cadastrar Usu√°rio</h3>
        <UserForm refreshUsers={fetchUsers} />
      </div>

      <div className="admin-panel">
        <h3 tabIndex="0">üìÑ Gerenciar Empr√©stimos</h3>
        <LoanForm onSave={fetchLoans} isAdmin={true} />
      </div>
    </div>

    <div className="admin-book-list">
      <h3 tabIndex="0">üìö Cat√°logo de Livros</h3>
      <BookList books={books} refreshBooks={fetchBooks} />
    </div>

    <div className="admin-user-list">
      <h3 tabIndex="0">Lista de Usu√°rios</h3>
      {users.length > 0 ? (
        <ul>
          {users.map((u) => (
            <li key={u.id}>
              {u.username} ({u.email}) {u.is_superuser && "- Superusu√°rio"}
            </li>
          ))}
        </ul>
      ) : (
        <p>Nenhum usu√°rio registrado</p>
      )}
    </div>
  </section>
);

export default function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);
  const [loadingUser, setLoadingUser] = useState(true);

  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loans, setLoans] = useState([]);

  const navigate = useNavigate ? useNavigate() : null;

  const handleLogout = () => {
    logout();
    setUser(null);
    setRole("guest");
    if (navigate) navigate("/");
  };

  const fetchBooks = async () => {
    const data = await getBooks();
    setBooks(Array.isArray(data) ? data : data.results || []);
  };

  const fetchUsers = async () => {
    if (role !== "admin" && role !== "superuser") return;
    const data = await getUsers();
    setUsers(data);
  };

  const fetchLoans = async () => {
    const data = await getLoans();
    setLoans(Array.isArray(data) ? data : data.results || []);
  };

  // Carrega usu√°rio ao iniciar
  useEffect(() => {
    const initUser = async () => {
      const token = localStorage.getItem("access_token");
      if (!token) {
        setRole("guest");
        setLoadingUser(false);
        return;
      }

      try {
        const userData = await getCurrentUser();
        setUser(userData);
        setRole(userData.is_superuser ? "superuser" : userData.is_staff ? "admin" : "user");
        fetchBooks();
        fetchUsers();
        fetchLoans();
      } catch (err) {
        console.error("Erro ao buscar usu√°rio:", err);
        handleLogout();
      } finally {
        setLoadingUser(false);
      }
    };

    initUser();
  }, []);

  if (loadingUser || role === null) return <Loader />;

  const isAdminOrSuperuser = role === "admin" || role === "superuser";

  return (
    <Router>
      <div className="app-container" aria-label="Sistema de Biblioteca Acess√≠vel">
        <VLibras forceOnload={true} />
        <AccessibilityBar />

        <header className="app-header" role="banner">
          <h1 tabIndex="0">üìö Biblioteca Online Acess√≠vel</h1>
          {user ? (
            <div className="user-info" role="navigation" aria-label="Sess√£o do usu√°rio logado">
              <p tabIndex="0">
                Bem-vindo, <strong>{user.username}</strong> (
                {role === "superuser"
                  ? "Superusu√°rio"
                  : role === "admin"
                  ? "Administrador"
                  : "Usu√°rio"}
                )
              </p>
              <button onClick={handleLogout} aria-label="Sair da conta">
                Sair
              </button>
              {isAdminOrSuperuser && (
                <button onClick={() => navigate("/admin")}>Ir para Painel</button>
              )}
            </div>
          ) : (
            <LoginForm
              setUser={setUser}
              setRole={setRole}
              onLoginSuccess={() => {
                fetchBooks();
                fetchUsers();
                fetchLoans();
              }}
            />
          )}
        </header>

        <main id="main-content" tabIndex="-1" role="main">
          <Routes>
            <Route path="/" element={<Home books={books} refreshBooks={fetchBooks} />} />
            <Route
              path="/admin"
              element={
                isAdminOrSuperuser ? (
                  <AdminPanel
                    books={books}
                    users={users}
                    fetchBooks={fetchBooks}
                    fetchUsers={fetchUsers}
                    fetchLoans={fetchLoans}
                    role={role}
                  />
                ) : (
                  <Navigate to="/" />
                )
              }
            />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </main>

        <footer className="app-footer" role="contentinfo">
          <p tabIndex="0">
            Desenvolvido com acessibilidade total ‚Äî suporte a Libras, teclado e contraste.
          </p>
        </footer>
      </div>
    </Router>
  );
}
