import React, { useState, useEffect } from "react";
import { getLoans, createLoan, returnLoan, getUsers, getBooks } from "../Api";

export default function LoanComponents({ role }) {
  const [loans, setLoans] = useState([]);
  const [users, setUsers] = useState([]);
  const [books, setBooks] = useState([]);

  const [selectedUser, setSelectedUser] = useState("");
  const [selectedBook, setSelectedBook] = useState("");

  const [userSearch, setUserSearch] = useState(""); // üîç busca usu√°rio
  const [bookSearch, setBookSearch] = useState(""); // üîç busca livro

  const loadAll = async () => {
    const [l, u, b] = await Promise.all([getLoans(), getUsers(), getBooks()]);
    setLoans(l);
    setUsers(u);
    setBooks(b);
  };

  useEffect(() => {
    loadAll();
  }, []);

  // üîç Filtrar usu√°rios
  const filteredUsers = users.filter((u) =>
    u.username.toLowerCase().includes(userSearch.toLowerCase())
  );

  // üîç Filtrar livros dispon√≠veis
  const filteredBooks = books
    .filter((b) => b.available)
    .filter((b) =>
      b.title.toLowerCase().includes(bookSearch.toLowerCase())
    );

  const handleCreateLoan = async () => {
    if (!selectedUser || !selectedBook) {
      alert("‚ö†Ô∏è Selecione usu√°rio e livro.");
      return;
    }

    const response = await createLoan({
      user: selectedUser,
      book: selectedBook,
    });

    if (response) {
      alert("üìö Empr√©stimo registrado com sucesso!");
      loadAll();
      setSelectedUser("");
      setSelectedBook("");
      setUserSearch("");
      setBookSearch("");
    }
  };

  const handleReturn = async (loanId) => {
    await returnLoan(loanId);
    alert("üîô Livro devolvido!");
    loadAll();
  };

  return (
    <div className="loan-admin-panel">
      <h2>üîÑ Gerenciar Empr√©stimos</h2>

      {(role === "admin" || role === "superuser") && (
        <section className="loan-form-section">
          <h3>üìò Registrar Empr√©stimo</h3>

          {/* ========================== */}
          {/* üîç Buscar usu√°rio */}
          {/* ========================== */}
          <label>Pesquisar usu√°rio:</label>
          <input
            type="text"
            placeholder="Digite o nome do usu√°rio..."
            value={userSearch}
            onChange={(e) => setUserSearch(e.target.value)}
            style={{
              padding: "0.5rem",
              marginBottom: "0.5rem",
              width: "100%",
            }}
          />

          {/* Lista filtrada */}
          <label>Selecionar usu√°rio:</label>
          <select
            value={selectedUser}
            onChange={(e) => setSelectedUser(e.target.value)}
          >
            <option value="">Selecione...</option>
            {filteredUsers.map((user) => (
              <option key={user.id} value={user.id}>
                {user.username} ({user.email})
              </option>
            ))}
          </select>

          {/* ========================== */}
          {/* üîç Buscar livro */}
          {/* ========================== */}
          <label>Pesquisar livro:</label>
          <input
            type="text"
            placeholder="Digite o nome do livro..."
            value={bookSearch}
            onChange={(e) => setBookSearch(e.target.value)}
            style={{
              padding: "0.5rem",
              marginBottom: "0.5rem",
              width: "100%",
            }}
          />

          {/* Lista filtrada */}
          <label>Selecionar livro:</label>
          <select
            value={selectedBook}
            onChange={(e) => setSelectedBook(e.target.value)}
          >
            <option value="">Selecione...</option>
            {filteredBooks.map((book) => (
              <option key={book.id} value={book.id}>
                {book.title} ‚Äî {book.author}
              </option>
            ))}
          </select>

          <button onClick={handleCreateLoan} className="btn-save">
            ‚ûï Registrar Empr√©stimo
          </button>
        </section>
      )}

      {/* ================================
          üìã LISTA DE EMPR√âSTIMOS
      ================================= */}
      <section className="loan-list-section">
        <h3>üìã Empr√©stimos Atuais</h3>

        {loans.length === 0 ? (
          <p>Nenhum empr√©stimo registrado.</p>
        ) : (
          <ul className="loan-list">
            {loans.map((loan) => (
              <li key={loan.id}>
                <strong>Usu√°rio:</strong> {loan.user_username} <br />
                <strong>Livro:</strong> {loan.book_title} <br />
                <strong>Data de Empr√©stimo:</strong> {loan.loan_date} <br />
                <strong>Devolu√ß√£o Prevista:</strong>{" "}
                {loan.expected_return_date || "N√£o definida"} <br />

                {(role === "admin" || role === "superuser") && (
                  <button
                    className="btn-return"
                    onClick={() => handleReturn(loan.id)}
                  >
                    üîô Registrar Devolu√ß√£o
                  </button>
                )}
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
}
