// src/components/BookCard.js
import React from "react";
import PropTypes from "prop-types";
import "./bookcard.css";
import noCover from "../assets/no-cover.png";
import LoanButton from "./LoanButton";

export default function BookCard({ book, refreshBooks }) {
  const coverUrl = book.cover
    ? book.cover.startsWith("http")
      ? book.cover
      : `http://127.0.0.1:8000${book.cover}`
    : noCover;

  return (
    <article
      className={`book-card ${!book.available ? "unavailable" : ""}`}
      role="region"
      aria-label={`Livro: ${book.title}. Autor: ${book.author || "Não informado"}. ${
        book.publication_year ? `Ano: ${book.publication_year}.` : ""
      } ${book.genre ? `Gênero: ${book.genre}.` : ""} ${
        book.available ? "Disponível" : "Emprestado"
      }`}
    >
      <img
        src={coverUrl}
        alt={`Capa do livro "${book.title}"`}
        className="book-cover"
      />

      <div className="book-info">
        <h2>{book.title}</h2>
        <p>
          <strong>Autor:</strong> {book.author || "Não informado"}
        </p>
        <p>
          <strong>Ano:</strong> {book.publication_year || "Não informado"}
        </p>
        {book.genre && (
          <p>
            <strong>Gênero:</strong> {book.genre}
          </p>
        )}
        <p>
          <strong>Status:</strong> {book.available ? "Disponível" : "Emprestado"}
        </p>

        {book.loans && book.loans.length > 0 && (
          <p>
            <strong>Empréstimos ativos:</strong>{" "}
            {book.loans.map((loan) => loan.user_username || loan.username).join(", ")}
          </p>
        )}

        {book.available && refreshBooks && (
          <LoanButton bookId={book.id} refreshBooks={refreshBooks} />
        )}
      </div>
    </article>
  );
}

BookCard.propTypes = {
  book: PropTypes.shape({
    id: PropTypes.number.isRequired,
    title: PropTypes.string.isRequired,
    author: PropTypes.string,
    publication_year: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
    genre: PropTypes.string,
    cover: PropTypes.string,
    available: PropTypes.bool,
    loans: PropTypes.arrayOf(
      PropTypes.shape({
        username: PropTypes.string,
        user_username: PropTypes.string,
      })
    ),
  }).isRequired,
  refreshBooks: PropTypes.func,
};
