import React, { useState, useEffect } from "react";
import "../assets/styles.css";
import {
  getUsers,
  createUser,
  deleteUser,
  resetPassword,
  getCurrentUser,
} from "../Api";

// üîß Fun√ß√£o auxiliar para formatar mensagens de erro
function formatErrorData(data) {
  if (!data) return null;
  if (typeof data === "string") return data;
  if (Array.isArray(data)) return data.join(" | ");
  if (typeof data === "object") {
    return Object.entries(data)
      .map(([k, v]) => (Array.isArray(v) ? `${k}: ${v.join(" ; ")}` : `${k}: ${v}`))
      .join(" ‚∏∫ ");
  }
  return String(data);
}

export default function UserAdminPanel() {
  const [currentUser, setCurrentUser] = useState(null);
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");
  const [loading, setLoading] = useState(false);
  const [actionLoading, setActionLoading] = useState(null);
  const [message, setMessage] = useState({ text: "", type: "" });
  const [newPassword, setNewPassword] = useState(null);

  const fetchUsersList = async () => {
    try {
      const data = await getUsers();
      const lista = data.results || data;
      setUsers(lista);
    } catch (err) {
      setMessage({ text: "‚ùå Erro ao buscar usu√°rios.", type: "error" });
    }
  };

  useEffect(() => {
    async function loadUser() {
      const user = await getCurrentUser();
      setCurrentUser(user);
      if (user?.is_staff || user?.is_superuser) {
        await fetchUsersList();
      }
    }
    loadUser();
  }, []);

  const isAdmin = currentUser?.is_staff || currentUser?.is_superuser;
  if (!isAdmin) {
    return <div style={{ color: "red", fontWeight: "bold" }}>‚ö†Ô∏è Acesso negado.</div>;
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password !== password2) {
      setMessage({ text: "‚ö†Ô∏è Senhas n√£o coincidem.", type: "error" });
      return;
    }
    setLoading(true);
    try {
      await createUser({ username, email, password });
      setMessage({ text: "‚úÖ Usu√°rio criado!", type: "success" });
      setUsername(""); setEmail(""); setPassword(""); setPassword2("");
      fetchUsersList();
    } catch (err) {
      setMessage({ text: `‚ùå ${formatErrorData(err.message)}`, type: "error" });
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Tem certeza que deseja excluir?")) return;
    setActionLoading(id);
    try {
      await deleteUser(id);
      setMessage({ text: "üóëÔ∏è Usu√°rio exclu√≠do!", type: "success" });
      fetchUsersList();
    } catch (err) {
      setMessage({ text: `‚ùå ${formatErrorData(err.message)}`, type: "error" });
    } finally {
      setActionLoading(null);
    }
  };

  const handleResetPassword = async (id) => {
    const novaSenha = Math.random().toString(36).slice(-8);
    setActionLoading(id);
    try {
      await resetPassword(id, novaSenha);
      setNewPassword(novaSenha);
      setMessage({ text: "‚úÖ Senha redefinida!", type: "success" });
    } catch (err) {
      setMessage({ text: `‚ùå ${formatErrorData(err.message)}`, type: "error" });
    } finally {
      setActionLoading(null);
    }
  };

  const filteredUsers = users.filter(
    (u) =>
      u.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (u.email && u.email.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div className="admin-panel">
      <h2>‚öôÔ∏è Painel Administrativo de Usu√°rios</h2>

      {message.text && (
        <p style={{ color: message.type === "error" ? "red" : "green" }}>
          {message.text}
        </p>
      )}

      {newPassword && (
        <div style={{ background: "#f0f0f0", padding: "10px", marginBottom: "10px" }}>
          üîë Nova senha gerada: <strong>{newPassword}</strong>
          <button onClick={() => navigator.clipboard.writeText(newPassword)}>üìã Copiar</button>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <h4>üë§ Criar Novo Usu√°rio</h4>
        <input placeholder="Usu√°rio" value={username} onChange={(e) => setUsername(e.target.value)} required />
        <input placeholder="E-mail" type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input placeholder="Senha" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <input placeholder="Confirmar senha" type="password" value={password2} onChange={(e) => setPassword2(e.target.value)} required />
        <button type="submit" disabled={loading}>{loading ? "Criando..." : "Criar Usu√°rio"}</button>
      </form>

      <input
        placeholder="üîç Buscar usu√°rios..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <table>
        <thead>
          <tr><th>ID</th><th>Usu√°rio</th><th>E-mail</th><th>A√ß√µes</th></tr>
        </thead>
        <tbody>
          {filteredUsers.map((u) => (
            <tr key={u.id}>
              <td>{u.id}</td>
              <td>{u.username}</td>
              <td>{u.email}</td>
              <td>
                <button onClick={() => handleResetPassword(u.id)} disabled={actionLoading === u.id}>
                  {actionLoading === u.id ? "Processando..." : "üîë Redefinir Senha"}
                </button>
                <button onClick={() => handleDelete(u.id)} disabled={actionLoading === u.id}>
                  {actionLoading === u.id ? "Excluindo..." : "‚ùå Excluir"}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
