// ‚úÖ src/Api.js
const API_URL = "http://127.0.0.1:8000/api/";

/**
 * üîπ Cabe√ßalhos com token JWT
 */
export function getAuthHeaders() {
  const token = localStorage.getItem("access_token");
  return token
    ? { Authorization: `Bearer ${token}`, "Content-Type": "application/json" }
    : { "Content-Type": "application/json" };
}

/**
 * üîπ Login ‚Äî gera tokens JWT
 */
export async function login(username, password) {
  const response = await fetch(`${API_URL}token/`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ username, password }),
  });

  if (!response.ok) throw new Error("Usu√°rio ou senha incorretos");

  const data = await response.json();
  localStorage.setItem("access_token", data.access);
  localStorage.setItem("refresh_token", data.refresh);
  console.log("üîë Tokens salvos com sucesso!");
  return data;
}

/**
 * üîπ Logout ‚Äî limpa tokens do navegador
 */
export function logout() {
  localStorage.removeItem("access_token");
  localStorage.removeItem("refresh_token");
}

/**
 * üîπ Obt√©m o usu√°rio atualmente autenticado
 */
export async function getCurrentUser() {
  const headers = getAuthHeaders();
  let response = await fetch(`${API_URL}users/me/`, { headers });

  // Se token expirou ‚Üí tenta atualizar
  if (response.status === 401) {
    const refreshed = await refreshToken();
    if (!refreshed) {
      console.warn("‚ö†Ô∏è Sess√£o expirada. Fa√ßa login novamente.");
      logout();
      return null;
    }
    response = await fetch(`${API_URL}users/me/`, { headers: getAuthHeaders() });
  }

  if (!response.ok) throw new Error("Erro ao buscar o usu√°rio atual");

  const data = await response.json();
  console.log("üë§ Usu√°rio atual:", data);
  return data;
}

/**
 * üîπ Atualiza o token de acesso usando o refresh token
 */
export async function refreshToken() {
  const refresh = localStorage.getItem("refresh_token");
  if (!refresh) return null;

  const response = await fetch(`${API_URL}token/refresh/`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ refresh }),
  });

  if (!response.ok) return null;

  const data = await response.json();
  localStorage.setItem("access_token", data.access);
  console.log("üîÅ Token renovado com sucesso!");
  return data.access;
}

/**
 * üîπ Lista todos os usu√°rios (requer admin/superuser)
 */
export async function getUsers() {
  const headers = getAuthHeaders();
  const response = await fetch(`${API_URL}users/`, { headers });
  if (!response.ok) throw new Error("Erro ao buscar usu√°rios");
  return response.json();
}

/**
 * üîπ Lista todos os livros dispon√≠veis
 */
export async function getBooks() {
  const response = await fetch(`${API_URL}books/`);
  if (!response.ok) throw new Error("Erro ao buscar livros");
  return response.json();
}

/**
 * üîπ Cria ou atualiza livro
 */
export async function saveBook(bookData) {
  const headers = getAuthHeaders();
  const method = bookData.id ? "PUT" : "POST";
  const url = bookData.id
    ? `${API_URL}books/${bookData.id}/`
    : `${API_URL}books/`;

  const response = await fetch(url, {
    method,
    headers,
    body: JSON.stringify(bookData),
  });

  if (!response.ok) throw new Error("Erro ao salvar livro");
  return response.json();
}

/**
 * üîπ Lista de empr√©stimos (loans)
 */
export async function getLoans() {
  const headers = getAuthHeaders();
  const response = await fetch(`${API_URL}loans/`, { headers });

  if (!response.ok) throw new Error("Erro ao buscar empr√©stimos");
  return response.json();
}
