import React, { useEffect, useState } from "react";
import BookGrid from "./BookGrid";
import { getBooks } from "../Api";
import { useAuth } from "../context/AuthContext";   // ‚úÖ Agora l√™ login direto do contexto
import "./booklist.css";

export default function BookList({ refreshBooksParent = null }) {
  const { user, loading } = useAuth();  // üî• Agora √© 100% confi√°vel: logado ou n√£o
  const [books, setBooks] = useState([]);
  const [fetchLoading, setFetchLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchBooks = async () => {
    if (!user) {
      setBooks([]);
      setError("üîí Fa√ßa login para acessar o cat√°logo de livros.");
      return;
    }

    setFetchLoading(true);
    setError("");

    try {
      const list = await getBooks();
      setBooks(Array.isArray(list) ? list : []);
      refreshBooksParent?.(list);
      console.log("üìö Livros carregados:", list);
    } catch (err) {
      console.error("Erro ao buscar livros:", err);
      setError("‚ùå N√£o foi poss√≠vel carregar os livros.");
    } finally {
      setFetchLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, [user]);   // üî• Atualiza quando loga/desloga

  const speakBook = (book) => {
    if (!book || !("speechSynthesis" in window)) return;
    const msg = new SpeechSynthesisUtterance(
      `Livro: ${book.title}. Autor: ${book.author}. Ano: ${book.publication_year}.`
    );
    window.speechSynthesis.speak(msg);
  };

  // üîπ Tela de carregamento (login ainda verificando)
  if (loading) return <p>‚è≥ Carregando usu√°rio...</p>;

  return (
    <section className="book-list" aria-label="Cat√°logo de livros">
      {fetchLoading && <p>‚è≥ Carregando livros...</p>}

      {!fetchLoading && !user && (
        <p className="error-message">üîí Fa√ßa login para acessar o cat√°logo de livros.</p>
      )}

      {!fetchLoading && user && books.length === 0 && !error && (
        <p>üì≠ Nenhum livro encontrado.</p>
      )}

      {!fetchLoading && user && !error && books.length > 0 && (
        <BookGrid
          books={books.map((b) => ({
            ...b,
            speak: () => speakBook(b),
            altText: `Capa do livro ${b.title}`,
          }))}
          refreshBooks={fetchBooks}
        />
      )}

      {!fetchLoading && error && <p className="error-message">{error}</p>}
    </section>
  );
}
