// src/components/BookGrid.js
import React from "react";
import PropTypes from "prop-types";
import BookCard from "./BookCard";
import "./bookgrid.css";

export default function BookGrid({ books = [], refreshBooks = null }) {
  // Mensagem quando não há livros
  if (!books || books.length === 0) {
    return (
      <p
        tabIndex="0"
        role="status"
        aria-live="polite"
        className="no-books-message"
      >
        Nenhum livro cadastrado no momento.
      </p>
    );
  }

  return (
    <div className="book-grid" role="list" aria-label="Lista de livros">
      {books.map((book) => (
        <div
          role="listitem"
          key={book.id || Math.random()} // fallback caso id não exista
        >
          <BookCard book={book} refreshBooks={refreshBooks} />
        </div>
      ))}
    </div>
  );
}

BookGrid.propTypes = {
  books: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
      title: PropTypes.string.isRequired,
      author: PropTypes.string,
      cover: PropTypes.string, // URL da capa
      speak: PropTypes.func, // função para leitura de áudio
      altText: PropTypes.string, // texto alternativo da capa
      available: PropTypes.bool,
      publication_year: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
      genre: PropTypes.string,
      loans: PropTypes.arrayOf(
        PropTypes.shape({
          username: PropTypes.string,
          user_username: PropTypes.string,
        })
      ),
    })
  ).isRequired,
  refreshBooks: PropTypes.func,
};
