// src/Api.js
const API_URL = "http://127.0.0.1:8000/api/";

/* =====================================================
   üîπ Fun√ß√µes auxiliares
   ===================================================== */
export function getAuthHeaders(contentType = "application/json") {
  const token = localStorage.getItem("access_token");
  return token
    ? { Authorization: `Bearer ${token}`, "Content-Type": contentType }
    : { "Content-Type": contentType };
}

async function normalizeResponse(response) {
  const data = await response.json();
  return Array.isArray(data) ? data : data.results || data.data || [];
}

async function handleProtectedRequest(url, options = {}) {
  let response = await fetch(url, options);
  if (response.status === 401) {
    const newToken = await refreshToken();
    if (!newToken) throw new Error("Sess√£o expirada. Fa√ßa login novamente.");
    // Atualiza headers com novo token
    options.headers = getAuthHeaders(options.headers?.["Content-Type"] || "application/json");
    response = await fetch(url, options);
  }
  return response;
}

/* =====================================================
   üîπ Autentica√ß√£o
   ===================================================== */
export async function login(username, password) {
  const response = await fetch(`${API_URL}token/`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ username, password }),
  });
  if (!response.ok) throw new Error("Usu√°rio ou senha incorretos");
  const data = await response.json();
  localStorage.setItem("access_token", data.access);
  localStorage.setItem("refresh_token", data.refresh);
  return data;
}

export function logout() {
  localStorage.removeItem("access_token");
  localStorage.removeItem("refresh_token");
  localStorage.removeItem("user_info");
}

export async function refreshToken() {
  const refresh = localStorage.getItem("refresh_token");
  if (!refresh) return null;
  const response = await fetch(`${API_URL}token/refresh/`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ refresh }),
  });
  if (!response.ok) {
    logout();
    return null;
  }
  const data = await response.json();
  localStorage.setItem("access_token", data.access);
  return data.access;
}

/* =====================================================
   üîπ Usu√°rio atual
   ===================================================== */
export async function getCurrentUser() {
  let headers = getAuthHeaders();
  let response = await fetch(`${API_URL}users/me/`, { headers });
  if (response.status === 401) {
    const newToken = await refreshToken();
    if (!newToken) return null;
    headers = getAuthHeaders();
    response = await fetch(`${API_URL}users/me/`, { headers });
  }
  if (!response.ok) throw new Error("Erro ao obter usu√°rio atual");
  const userData = await response.json();
  localStorage.setItem("user_info", JSON.stringify(userData));
  return userData;
}

/* =====================================================
   üîπ CRUD Usu√°rios
   ===================================================== */
export async function getUsers() {
  const response = await handleProtectedRequest(`${API_URL}users/`, { headers: getAuthHeaders() });
  if (!response.ok) throw new Error("Erro ao buscar usu√°rios");
  return response.json();
}

export async function createUser(userData) {
  const response = await handleProtectedRequest(`${API_URL}users/`, {
    method: "POST",
    headers: getAuthHeaders(),
    body: JSON.stringify(userData),
  });
  if (!response.ok) throw new Error(`Erro ao criar usu√°rio: ${await response.text()}`);
  return response.json();
}

export async function deleteUser(userId) {
  const response = await handleProtectedRequest(`${API_URL}users/${userId}/`, {
    method: "DELETE",
    headers: getAuthHeaders(),
  });
  if (!response.ok) throw new Error(`Erro ao deletar usu√°rio: ${await response.text()}`);
  return true;
}

export async function resetPassword(userId, newPassword) {
  const response = await handleProtectedRequest(`${API_URL}users/${userId}/reset-password/`, {
    method: "PUT",
    headers: getAuthHeaders(),
    body: JSON.stringify({ password: newPassword }),
  });
  if (!response.ok) throw new Error(`Erro ao redefinir senha: ${await response.text()}`);
  return response.json();
}

/* =====================================================
   üîπ Livros
   ===================================================== */
export async function getBooks() {
  const response = await handleProtectedRequest(`${API_URL}books/`, { headers: getAuthHeaders() });
  if (!response.ok) throw new Error("Erro ao buscar livros");
  return normalizeResponse(response);
}

export async function createBook(formData) {
  const token = localStorage.getItem("access_token");
  const response = await handleProtectedRequest(`${API_URL}books/`, {
    method: "POST",
    headers: { Authorization: `Bearer ${token}` }, // N√£o define Content-Type para FormData
    body: formData,
  });
  if (!response.ok) throw new Error(`Erro ao cadastrar livro: ${await response.text()}`);
  return response.json();
}

export async function saveBook(bookData) {
  const method = bookData.id ? "PUT" : "POST";
  const endpoint = bookData.id ? `${API_URL}books/${bookData.id}/` : `${API_URL}books/`;
  const response = await handleProtectedRequest(endpoint, {
    method,
    headers: getAuthHeaders(),
    body: JSON.stringify(bookData),
  });
  if (!response.ok) throw new Error(`Erro ao salvar livro: ${await response.text()}`);
  return response.json();
}

/* =====================================================
   üîπ Empr√©stimos
   ===================================================== */
export async function getLoans() {
  const response = await handleProtectedRequest(`${API_URL}loans/`, { headers: getAuthHeaders() });
  if (!response.ok) throw new Error("Erro ao buscar empr√©stimos");
  return response.json();
}

export async function createLoan(loanData) {
  const response = await handleProtectedRequest(`${API_URL}loans/`, {
    method: "POST",
    headers: getAuthHeaders(),
    body: JSON.stringify(loanData), // { book_id, user_id, turma }
  });
  if (!response.ok) throw new Error(`Erro ao registrar empr√©stimo: ${await response.text()}`);
  return response.json();
}

export async function returnBook(returnData) {
  const { loan_id, turma } = returnData;
  const response = await handleProtectedRequest(`${API_URL}loans/${loan_id}/`, {
    method: "PATCH",
    headers: getAuthHeaders(),
    body: JSON.stringify({ returned: true, turma }),
  });
  if (!response.ok) throw new Error(`Erro ao registrar devolu√ß√£o: ${await response.text()}`);
  return response.json();
}
