import React, { useEffect, useState } from "react";
import { createLoan, getBooks, getLoans, getUsers, returnBook } from "../Api";

export default function LoanForm({ onLoanCreated }) {
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loans, setLoans] = useState([]);
  const [bookId, setBookId] = useState("");
  const [userId, setUserId] = useState("");
  const [loanId, setLoanId] = useState("");
  const [loanDate, setLoanDate] = useState("");
  const [turma, setTurma] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError("");
      try {
        const booksRes = await getBooks();
        setBooks(Array.isArray(booksRes) ? booksRes : booksRes.results || []);

        const usersRes = await getUsers();
        console.log("Usu√°rios carregados:", usersRes);
        setUsers(Array.isArray(usersRes) ? usersRes : usersRes.results || []);

        const loansRes = await getLoans();
        const activeLoans = Array.isArray(loansRes)
          ? loansRes.filter((loan) => !loan.returned)
          : loansRes.results
          ? loansRes.results.filter((loan) => !loan.returned)
          : [];
        setLoans(activeLoans);
      } catch (err) {
        setError("‚ö†Ô∏è Erro ao carregar dados.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleLoanSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    try {
      const payload = {
        book_id: parseInt(bookId),
        user_id: parseInt(userId), // ‚úÖ Agora envia user_id
        turma,
        data_emprestimo: loanDate || new Date().toISOString().split("T")[0],
      };

      const response = await createLoan(payload);
      console.log("Resposta do servidor:", response);

      setSuccess("‚úÖ Empr√©stimo registrado!");
      setBookId("");
      setUserId("");
      setLoanDate("");
      setTurma("");
      if (onLoanCreated) onLoanCreated();
    } catch (err) {
      setError(`‚ùå Erro ao registrar empr√©stimo: ${err.message}`);
      console.error("Detalhes do erro:", err);
    }
  };

  const handleReturnSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    try {
      const payload = { loan_id: parseInt(loanId), user_id: parseInt(userId), turma };
      const response = await returnBook(payload);
      console.log("Resposta do servidor:", response);

      setSuccess("‚úÖ Livro devolvido!");
      setLoanId("");
      setUserId("");
      setTurma("");
      if (onLoanCreated) onLoanCreated();
    } catch (err) {
      setError(`‚ùå Erro ao devolver livro: ${err.message}`);
      console.error("Detalhes do erro:", err);
    }
  };

  if (loading) return <p>‚è≥ Carregando dados...</p>;

  return (
    <section className="loan-form">
      <h2>üìö Registrar Empr√©stimo</h2>
      <form onSubmit={handleLoanSubmit}>
        <label>Usu√°rio:</label>
        <select value={userId} onChange={(e) => setUserId(e.target.value)} required>
          <option value="">Selecione um usu√°rio</option>
          {users.length > 0 ? (
            users.map((user) => (
              <option key={user.id} value={user.id}>{user.name}</option>
            ))
          ) : (
            <option disabled>Nenhum usu√°rio dispon√≠vel</option>
          )}
        </select>

        <label>Turma:</label>
        <input
          type="text"
          value={turma}
          onChange={(e) => setTurma(e.target.value)}
          placeholder="Digite a turma"
          required
        />

        <label>Livro:</label>
        <select value={bookId} onChange={(e) => setBookId(e.target.value)} required>
          <option value="">Selecione um livro</option>
          {books.length > 0 ? (
            books.map((book) => (
              <option key={book.id} value={book.id}>{book.title}</option>
            ))
          ) : (
            <option disabled>Nenhum livro dispon√≠vel</option>
          )}
        </select>

        <label>Data do Empr√©stimo:</label>
        <input type="date" value={loanDate} onChange={(e) => setLoanDate(e.target.value)} />
        <button type="submit">Salvar Empr√©stimo</button>
      </form>

      <h2>üîÑ Registrar Devolu√ß√£o</h2>
      <form onSubmit={handleReturnSubmit}>
        <label>Usu√°rio:</label>
        <select value={userId} onChange={(e) => setUserId(e.target.value)} required>
          <option value="">Selecione um usu√°rio</option>
          {users.length > 0 ? (
            users.map((user) => (
              <option key={user.id} value={user.id}>{user.name}</option>
            ))
          ) : (
            <option disabled>Nenhum usu√°rio dispon√≠vel</option>
          )}
        </select>

        <label>Turma:</label>
        <input
          type="text"
          value={turma}
          onChange={(e) => setTurma(e.target.value)}
          placeholder="Digite a turma"
          required
        />

        <label>Empr√©stimo:</label>
        <select value={loanId} onChange={(e) => setLoanId(e.target.value)} required>
          <option value="">Selecione um empr√©stimo</option>
          {loans.length > 0 ? (
            loans.map((loan) => (
              <option key={loan.id} value={loan.id}>{loan.book?.title}</option>
            ))
          ) : (
            <option disabled>Nenhum empr√©stimo ativo</option>
          )}
        </select>
        <button type="submit">Devolver Livro</button>
      </form>

      {error && <p className="form-message error">{error}</p>}
      {success && <p className="form-message success">{success}</p>}
    </section>
  );
}
