// src/App.js
import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import VLibras from "@djpfs/react-vlibras";

import {
  getBooks,
  getUsers,
  getLoans,
  getCurrentUser,
  login,
  logout,
  refreshAccessToken,
} from "./Api";

import BookList from "./components/BookList";
import BookForm from "./components/BookForm";
import UserForm from "./components/UserForm";
import LoanForm from "./components/LoanForm";
import AccessibilityBar from "./components/AccessibilityBar";

import "./App.css";

const Loader = () => <p>Carregando sistema...</p>;

const Home = ({ books, fetchBooks }) => (
  <section aria-label="Cat√°logo de livros">
    <h2 tabIndex="0">üìñ Cat√°logo de Livros</h2>
    <BookList books={books} refreshBooks={fetchBooks} />
  </section>
);

function LoginForm({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    try {
      await login(username, password);
      onLogin();
    } catch (err) {
      setMessage("‚ùå Falha no login. Verifique usu√°rio e senha.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="login-form" aria-label="Login de usu√°rio">
      <h3 tabIndex="0">üîê Acesso ao Sistema</h3>
      {message && <p className="form-message error" role="alert">{message}</p>}
      <label htmlFor="username">Usu√°rio:</label>
      <input id="username" type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
      <label htmlFor="password">Senha:</label>
      <input id="password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
      <button type="submit" disabled={loading}>
        {loading ? "Entrando..." : "Entrar"}
      </button>
    </form>
  );
}

const AdminPanel = ({ role, books, users, fetchBooks, fetchUsers, fetchLoans }) => (
  <section aria-label="√Årea de administra√ß√£o">
    <h2 tabIndex="0">‚öôÔ∏è Painel Administrativo</h2>

    {(role === "superuser" || role === "admin") && (
      <a
        href="http://127.0.0.1:8000/admin/"
        target="_blank"
        rel="noopener noreferrer"
        className="admin-link"
      >
        ‚öôÔ∏è Acessar Django Admin
      </a>
    )}

    <div className="admin-top-panels">
      <div className="admin-panel">
        <BookForm refreshBooks={fetchBooks} />
      </div>

      <div className="admin-panel">
        <UserForm refreshUsers={fetchUsers} />
      </div>

      <div className="admin-panel">
        <LoanForm onSave={fetchLoans} isAdmin={true} />
      </div>
    </div>

    <div className="admin-book-list">
      <h3 tabIndex="0">üìö Livros Registrados</h3>
      <BookList books={books} refreshBooks={fetchBooks} />
    </div>

    <div className="admin-user-list">
      <h3 tabIndex="0">üë• Usu√°rios</h3>
      {users.length ? (
        <ul>
          {users.map((u) => (
            <li key={u.id}>
              {u.username} ({u.email}) {u.is_superuser && "‚≠ê Superusu√°rio"}
            </li>
          ))}
        </ul>
      ) : (
        <p>Nenhum usu√°rio registrado.</p>
      )}
    </div>
  </section>
);

export default function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState("guest");
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loans, setLoans] = useState([]);
  const [loadingUser, setLoadingUser] = useState(true);

  const handleLogout = () => {
    logout();
    setUser(null);
    setRole("guest");
  };

  const fetchBooks = async () => {
    try {
      const data = await getBooks();
      setBooks(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao buscar livros:", err);
    }
  };

  const fetchUsers = async () => {
    if (role !== "admin" && role !== "superuser") return;
    try {
      const data = await getUsers();
      setUsers(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao buscar usu√°rios:", err);
    }
  };

  const fetchLoans = async () => {
    try {
      const data = await getLoans();
      setLoans(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao buscar empr√©stimos:", err);
    }
  };

  // üîπ Fun√ß√£o que carrega dados do usu√°rio com refresh de token
  const loadUserData = async () => {
    setLoadingUser(true);
    const token = localStorage.getItem("access_token");
    if (!token) {
      setRole("guest");
      setLoadingUser(false);
      return;
    }

    try {
      let userData;
      try {
        userData = await getCurrentUser();
      } catch (err) {
        console.log("Token expirado, tentando refresh...");
        const newToken = await refreshAccessToken();
        if (newToken) {
          userData = await getCurrentUser();
        } else {
          handleLogout();
          setLoadingUser(false);
          return;
        }
      }

      console.log("üë§ Usu√°rio carregado:", userData);
      setUser(userData);

      const newRole = userData.is_superuser
        ? "superuser"
        : userData.is_staff
        ? "admin"
        : "user";
      setRole(newRole);

      if (newRole === "admin" || newRole === "superuser") {
        await Promise.all([fetchBooks(), fetchLoans(), fetchUsers()]);
      } else {
        await Promise.all([fetchBooks(), fetchLoans()]);
      }

    } catch (err) {
      console.error("Erro ao carregar usu√°rio:", err);
      handleLogout();
    } finally {
      setLoadingUser(false);
    }
  };

  useEffect(() => {
    loadUserData();
  }, []);

  if (loadingUser) return <Loader />;

  const isAdmin = role === "admin" || role === "superuser";

  return (
    <Router>
      <div className="app-container">
        <VLibras forceOnload />
        <AccessibilityBar />

        <header className="app-header">
          <h1 tabIndex="0">üìö Biblioteca Online Acess√≠vel</h1>
          {user ? (
            <div className="user-info">
              <p tabIndex="0">
                Bem-vindo, <strong>{user.username}</strong> (
                {role === "superuser"
                  ? "Superusu√°rio"
                  : role === "admin"
                  ? "Administrador"
                  : "Usu√°rio"} )
              </p>
              <button onClick={handleLogout}>Sair</button>
            </div>
          ) : (
            <LoginForm onLogin={loadUserData} />
          )}
        </header>

        <main id="main-content" role="main">
          <Routes>
            <Route path="/" element={<Home books={books} fetchBooks={fetchBooks} />} />
            <Route
              path="/admin"
              element={
                isAdmin ? (
                  <AdminPanel
                    role={role}
                    books={books}
                    users={users}
                    fetchBooks={fetchBooks}
                    fetchUsers={fetchUsers}
                    fetchLoans={fetchLoans}
                  />
                ) : (
                  <Navigate to="/" />
                )
              }
            />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </main>

        <footer className="app-footer" role="contentinfo">
          <p tabIndex="0">
            Desenvolvido com acessibilidade total ‚Äî suporte a Libras, teclado e alto contraste.
          </p>
        </footer>
      </div>
    </Router>
  );
}
