import React, { useState } from "react";
import { createBook } from "../Api";

export default function BookForm({ onSave }) {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [cover, setCover] = useState(null);
  const [message, setMessage] = useState("");

  const isSuperUser = JSON.parse(localStorage.getItem("user_info"))?.is_superuser;

  if (!isSuperUser) {
    return <p className="access-denied">⚠️ Acesso negado. Apenas superusuários podem cadastrar livros.</p>;
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title || !author) {
      setMessage("⚠️ Preencha título e autor.");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("author", author);
    if (cover) formData.append("cover", cover);

    try {
      await createBook(formData);
      setMessage("✅ Livro cadastrado com sucesso!");
      setTitle("");
      setAuthor("");
      setCover(null);
      onSave?.();
    } catch (error) {
      setMessage(`❌ Erro: ${error.message}`);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="book-form">
      <h3>➕ Cadastrar Livro</h3>
      {message && <p>{message}</p>}
      <input type="text" placeholder="Título" value={title} onChange={(e) => setTitle(e.target.value)} />
      <input type="text" placeholder="Autor" value={author} onChange={(e) => setAuthor(e.target.value)} />
      <input type="file" onChange={(e) => setCover(e.target.files[0])} />
      <button type="submit">Salvar</button>
    </form>
  );
}
