import React, { useEffect, useState } from "react";
import { createLoan, getBooks, getLoans, returnBook, getAuthHeaders } from "../Api";

export default function LoanForm({ onLoanCreated }) {
  const [books, setBooks] = useState([]); // Lista de livros
  const [loans, setLoans] = useState([]); // Lista de empr√©stimos ativos
  const [bookId, setBookId] = useState("");
  const [loanId, setLoanId] = useState("");
  const [loanDate, setLoanDate] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // üîπ Carrega livros e empr√©stimos ativos
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError("");
      try {
        const headers = getAuthHeaders();

        const booksRes = await getBooks(headers);
        console.log("Livros recebidos:", booksRes.data);
        setBooks(Array.isArray(booksRes.data) ? booksRes.data : booksRes.data.results || []);

        const loansRes = await getLoans(headers);
        console.log("Empr√©stimos recebidos:", loansRes.data);
        const activeLoans = Array.isArray(loansRes.data)
          ? loansRes.data.filter((loan) => !loan.returned)
          : loansRes.data.results
          ? loansRes.data.results.filter((loan) => !loan.returned)
          : [];
        setLoans(activeLoans);
      } catch (err) {
        console.error("Erro ao carregar dados:", err);
        setError("‚ö†Ô∏è Erro ao carregar dados. Verifique sua conex√£o ou login.");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // üîπ Registrar empr√©stimo
  const handleLoanSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    try {
      await createLoan(
        {
          book_id: bookId,
          data_emprestimo: loanDate || new Date().toISOString().split("T")[0],
        },
        getAuthHeaders()
      );
      setSuccess("‚úÖ Empr√©stimo registrado!");
      setBookId("");
      setLoanDate("");
      if (onLoanCreated) onLoanCreated();
    } catch (err) {
      console.error("Erro ao registrar empr√©stimo:", err.response?.data || err);
      setError("‚ùå Erro ao registrar empr√©stimo.");
    }
  };

  // üîπ Registrar devolu√ß√£o
  const handleReturnSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    try {
      await returnBook(loanId, getAuthHeaders());
      setSuccess("‚úÖ Livro devolvido!");
      setLoanId("");
      if (onLoanCreated) onLoanCreated();
    } catch (err) {
      console.error("Erro ao devolver livro:", err.response?.data || err);
      setError("‚ùå Erro ao devolver livro.");
    }
  };

  if (loading) return <p>‚è≥ Carregando dados...</p>;

  return (
    <section className="loan-form">
      <h2>üìö Registrar Empr√©stimo</h2>
      <form onSubmit={handleLoanSubmit}>
        <label>Livro:</label>
        <select value={bookId} onChange={(e) => setBookId(e.target.value)} required>
          <option value="">Selecione um livro</option>
          {books.length > 0 ? (
            books.map((book) => (
              <option key={book.id} value={book.id}>{book.title}</option>
            ))
          ) : (
            <option disabled>Nenhum livro dispon√≠vel</option>
          )}
        </select>
        <label>Data do Empr√©stimo:</label>
        <input type="date" value={loanDate} onChange={(e) => setLoanDate(e.target.value)} />
        <button type="submit">Salvar Empr√©stimo</button>
      </form>

      <h2>üîÑ Registrar Devolu√ß√£o</h2>
      <form onSubmit={handleReturnSubmit}>
        <label>Empr√©stimo:</label>
        <select value={loanId} onChange={(e) => setLoanId(e.target.value)} required>
          <option value="">Selecione um empr√©stimo</option>
          {loans.length > 0 ? (
            loans.map((loan) => (
              <option key={loan.id} value={loan.id}>{loan.book?.title}</option>
            ))
          ) : (
            <option disabled>Nenhum empr√©stimo ativo</option>
          )}
        </select>
        <button type="submit">Devolver Livro</button>
      </form>

      {error && <p className="form-message error">{error}</p>}
      {success && <p className="form-message success">{success}</p>}
    </section>
  );
}
