// src/components/LoanComponents.js
import React, { useEffect, useState } from "react";
import {
  getLoans,
  createLoan,
  returnBook,
  getUsers,
  getBooks
} from "../Api";

export default function LoanComponents({ role, currentUser }) {
  const [loans, setLoans] = useState([]);
  const [users, setUsers] = useState([]);
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);

  // Campos do cadastro
  const [selectedUser, setSelectedUser] = useState("");
  const [userSearch, setUserSearch] = useState("");

  const [selectedBook, setSelectedBook] = useState("");
  const [bookSearch, setBookSearch] = useState("");

  const isAdmin = role === "admin" || role === "superuser";
  const isUser = role === "user";

  const loadData = async () => {
    setLoading(true);
    try {
      const loansData = await getLoans();
      const booksData = await getBooks();
      const usersData = await getUsers();

      setLoans(Array.isArray(loansData) ? loansData : loansData.results || []);
      setBooks(booksData);
      setUsers(usersData);
    } catch (err) {
      console.error("Erro ao carregar dados do servidor:", err);
    }
    setLoading(false);
  };

  useEffect(() => {
    loadData();
  }, []);

  // üîπ Filtragem din√¢mica: usu√°rio pode digitar OU escolher
  const filteredUsers = users.filter((u) =>
    u.username.toLowerCase().includes(userSearch.toLowerCase())
  );

  const filteredBooks = books.filter((b) =>
    (b.title + " " + (b.author || ""))
      .toLowerCase()
      .includes(bookSearch.toLowerCase())
  );

  // üîπ Registrar empr√©stimo
  const handleLoan = async () => {
    if (!selectedUser || !selectedBook) {
      alert("‚ö†Ô∏è Preencha usu√°rio e livro.");
      return;
    }
    try {
      await createLoan({
        user_id: Number(selectedUser),
        book_id: Number(selectedBook)
      });
      alert("üìö Empr√©stimo registrado com sucesso!");
      loadData();
    } catch (err) {
      console.error(err);
      alert("‚ùå Erro ao registrar empr√©stimo.");
    }
  };

  // üîπ Registrar devolu√ß√£o
  const handleReturn = async (loanId) => {
    try {
      await returnBook({ loan_id: loanId });
      alert("üîô Livro devolvido!");
      loadData();
    } catch (err) {
      console.error(err);
      alert("‚ùå Erro ao registrar devolu√ß√£o.");
    }
  };

  // üîπ Usu√°rio comum s√≥ v√™ seus empr√©stimos
  const visibleLoans = isUser
    ? loans.filter((l) => l.user?.id === currentUser.id)
    : loans;

  return (
    <section className="loan-panel">
      <h2>üìö Empr√©stimos</h2>

      {/* ADMIN / SUPERUSER ‚Üí Cadastrar empr√©stimo */}
      {isAdmin && (
        <div className="loan-create-box">
          <h3>‚ûï Registrar Empr√©stimo</h3>

          {/* Campo de usu√°rio: digitar + selecionar */}
          <label>Usu√°rio:</label>
          <input
            type="text"
            placeholder="Digite o nome..."
            value={userSearch}
            onChange={(e) => setUserSearch(e.target.value)}
          />
          <select
            value={selectedUser}
            onChange={(e) => setSelectedUser(e.target.value)}
          >
            <option value="">-- Selecione --</option>
            {filteredUsers.map((u) => (
              <option key={u.id} value={u.id}>
                {u.username} ({u.email})
              </option>
            ))}
          </select>

          {/* Campo de livro: digitar + selecionar */}
          <label>Livro:</label>
          <input
            type="text"
            placeholder="Digite t√≠tulo ou autor..."
            value={bookSearch}
            onChange={(e) => setBookSearch(e.target.value)}
          />
          <select
            value={selectedBook}
            onChange={(e) => setSelectedBook(e.target.value)}
          >
            <option value="">-- Selecione --</option>
            {filteredBooks.map((b) => (
              <option key={b.id} value={b.id}>
                {b.title} ‚Äî {b.author}{" "}
                {!b.available && "(Indispon√≠vel)"}
              </option>
            ))}
          </select>

          <button onClick={handleLoan} className="btn-primary">
            Registrar Empr√©stimo
          </button>
        </div>
      )}

      {/* Lista de empr√©stimos */}
      <h3>üìÑ Empr√©stimos Registrados</h3>

      {loading ? (
        <p>‚è≥ Carregando...</p>
      ) : visibleLoans.length === 0 ? (
        <p>üì≠ Nenhum empr√©stimo encontrado.</p>
      ) : (
        <table className="loan-table">
          <thead>
            <tr>
              <th>Livro</th>
              <th>Usu√°rio</th>
              <th>Data</th>
              <th>Devolu√ß√£o</th>
              {isAdmin && <th>A√ß√µes</th>}
            </tr>
          </thead>
          <tbody>
            {visibleLoans.map((l) => (
              <tr key={l.id}>
                <td>{l.book?.title}</td>
                <td>{l.user?.username}</td>
                <td>{new Date(l.loan_date).toLocaleDateString()}</td>
                <td>
                  {l.return_date
                    ? new Date(l.return_date).toLocaleDateString()
                    : "‚Äî"}
                </td>
                {isAdmin && (
                  <td>
                    {!l.return_date && (
                      <button
                        className="btn-return"
                        onClick={() => handleReturn(l.id)}
                      >
                        üîô Devolver
                      </button>
                    )}
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </section>
  );
}
