// src/components/BookList.js
import React, { useEffect, useState } from "react";
import { getBooks } from "../Api"; // ‚úÖ Corrigido caminho da API
import BookGrid from "./BookGrid";
import "./booklist.css";

export default function BookList({ refreshBooksParent = null, isLoggedIn = false }) {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  /* =====================================================
     üîπ Fun√ß√£o principal ‚Äî busca livros do backend
     ===================================================== */
  const fetchBooks = async () => {
    const token = localStorage.getItem("access_token");

    if (!token) {
      setBooks([]);
      setError("üîí Fa√ßa login para acessar o cat√°logo de livros.");
      return;
    }

    setLoading(true);
    setError("");

    try {
      const booksData = await getBooks();

      const list = Array.isArray(booksData)
        ? booksData
        : booksData?.results || [];

      setBooks(list);
      if (refreshBooksParent) refreshBooksParent(list);

      console.log("üìö Livros carregados com sucesso:", list);
    } catch (err) {
      console.error("Erro ao buscar livros:", err);
      setError("‚ùå N√£o foi poss√≠vel carregar os livros. Verifique o servidor ou o token.");
    } finally {
      setLoading(false);
    }
  };

  /* =====================================================
     üîπ Atualiza livros sempre que o login muda
     ===================================================== */
  useEffect(() => {
    if (isLoggedIn) fetchBooks();
    else {
      setBooks([]);
      setError("üîí Fa√ßa login para acessar o cat√°logo de livros.");
    }
  }, [isLoggedIn]);

  /* =====================================================
     üîπ Leitura em voz alta (acessibilidade)
     ===================================================== */
  const speakBook = (book) => {
    if (!book || !("speechSynthesis" in window)) return;

    const msg = new SpeechSynthesisUtterance(
      `Livro: ${book.title || "n√£o informado"}. ` +
      `Autor: ${book.author || "n√£o informado"}. ` +
      `Ano: ${book.publication_year || "n√£o informado"}. ` +
      `${book.genre ? `G√™nero: ${book.genre}. ` : ""}` +
      `Status: ${book.available ? "Dispon√≠vel" : "Emprestado"}.`
    );
    window.speechSynthesis.speak(msg);
  };

  /* =====================================================
     üîπ Renderiza√ß√£o
     ===================================================== */
  return (
    <section className="book-list" aria-label="Cat√°logo de livros">
      {loading && (
        <p tabIndex="0" aria-live="polite">
          ‚è≥ Carregando livros...
        </p>
      )}

      {!loading && error && (
        <p tabIndex="0" aria-live="assertive" className="error-message">
          {error}
        </p>
      )}

      {!loading && !error && books.length === 0 && isLoggedIn && (
        <p tabIndex="0" aria-live="polite">
          üì≠ Nenhum livro encontrado no cat√°logo.
        </p>
      )}

      {!loading && !error && books.length > 0 && (
        <BookGrid
          books={books.map((b) => ({
            ...b,
            speak: () => speakBook(b),
            altText: `Capa do livro ${b.title || "sem t√≠tulo"}`,
          }))}
          refreshBooks={fetchBooks}
        />
      )}
    </section>
  );
}
