import React, { useState, useEffect } from "react";
import axios from "axios";
import "../App.css";

export default function LoanForm({ isAdmin = false, role, onSave }) {
  const [users, setUsers] = useState([]);
  const [books, setBooks] = useState([]);
  const [loans, setLoans] = useState([]);
  const [selectedUser, setSelectedUser] = useState("");
  const [selectedBook, setSelectedBook] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ text: "", type: "" });

  const token = localStorage.getItem("access_token");
  const authHeader = { headers: { Authorization: `Bearer ${token}` } };

  // ‚úÖ Bloqueia acesso se n√£o for superusu√°rio
  const accessDenied = isAdmin && role !== "superuser";

  // üîπ Carrega dados iniciais (somente se permitido)
  const fetchData = async () => {
    if (accessDenied) return; // Prote√ß√£o extra
    setLoading(true);
    try {
      const [usersRes, booksRes, loansRes] = await Promise.all([
        isAdmin
          ? axios.get("http://127.0.0.1:8000/api/users/", authHeader)
          : Promise.resolve({ data: [] }),
        axios.get("http://127.0.0.1:8000/api/books/", authHeader),
        axios.get("http://127.0.0.1:8000/api/loans/", authHeader),
      ]);

      setUsers(Array.isArray(usersRes.data) ? usersRes.data : []);
      setBooks(
        (Array.isArray(booksRes.data) ? booksRes.data : []).filter(
          (b) => b.available !== false
        )
      );
      setLoans(Array.isArray(loansRes.data) ? loansRes.data : []);
    } catch (err) {
      console.error("Erro ao carregar dados:", err);
      setMessage({ text: "‚ùå Erro ao carregar dados do servidor.", type: "error" });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // üîπ Registrar novo empr√©stimo
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (accessDenied) {
      setMessage({ text: "‚ö†Ô∏è Acesso negado.", type: "error" });
      return;
    }
    if ((isAdmin && !selectedUser) || !selectedBook) {
      setMessage({ text: "‚ö†Ô∏è Selecione usu√°rio e livro.", type: "error" });
      return;
    }

    setLoading(true);
    try {
      await axios.post(
        "http://127.0.0.1:8000/api/loans/",
        isAdmin
          ? { user: selectedUser, book: selectedBook }
          : { book: selectedBook },
        authHeader
      );
      setMessage({ text: "‚úÖ Empr√©stimo registrado!", type: "success" });
      setSelectedUser("");
      setSelectedBook("");
      fetchData();
      onSave?.();
    } catch (err) {
      console.error("Erro ao registrar empr√©stimo:", err);
      setMessage({ text: "‚ùå Erro ao registrar empr√©stimo.", type: "error" });
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ Se acesso negado, mostra aviso e n√£o renderiza formul√°rio
  if (accessDenied) {
    return (
      <p tabIndex="0" role="alert" className="access-denied">
        ‚ö†Ô∏è Acesso negado. Apenas superusu√°rios podem gerenciar empr√©stimos.
      </p>
    );
  }

  return (
    <div className={`loan-form-container ${isAdmin ? "admin" : "user"}`}>
      {/* Formul√°rio */}
      <form onSubmit={handleSubmit} aria-label="Formul√°rio de empr√©stimo">
        <h3 tabIndex="0">
          {isAdmin ? "üìö Gerenciar Empr√©stimos" : "üìö Solicitar Empr√©stimo"}
        </h3>

        {message.text && (
          <p role="status" tabIndex="0" className={`form-message ${message.type}`}>
            {message.text}
          </p>
        )}

        {isAdmin && (
          <>
            <label htmlFor="user">Usu√°rio:</label>
            <select
              id="user"
              value={selectedUser}
              onChange={(e) => setSelectedUser(e.target.value)}
              required
            >
              <option value="">-- Selecione um usu√°rio --</option>
              {users.map((u) => (
                <option key={u.id} value={u.id}>
                  {u.username} ({u.email})
                </option>
              ))}
            </select>
          </>
        )}

        <label htmlFor="book">Livro:</label>
        <select
          id="book"
          value={selectedBook}
          onChange={(e) => setSelectedBook(e.target.value)}
          required
        >
          <option value="">-- Selecione um livro dispon√≠vel --</option>
          {books.map((b) => (
            <option key={b.id} value={b.id}>
              {b.title}
            </option>
          ))}
        </select>

        <button type="submit" disabled={loading}>
          {loading
            ? "Processando..."
            : isAdmin
            ? "Registrar Empr√©stimo"
            : "Solicitar Empr√©stimo"}
        </button>
      </form>

      {/* Lista de empr√©stimos */}
      <section className="loan-list" aria-label="Empr√©stimos ativos">
        <h3 tabIndex="0">üìÑ Empr√©stimos Ativos</h3>
        {loans.filter((l) => !l.returned).length === 0 ? (
          <p tabIndex="0">Nenhum empr√©stimo ativo.</p>
        ) : (
          <ul>
            {loans
              .filter((l) => !l.returned)
              .map((l) => (
                <li key={l.id} tabIndex="0">
                  {l.book?.title} ‚Äî {l.user?.username}
                  {isAdmin && (
                    <button
                      onClick={() => handleReturn(l.id)}
                      disabled={loading}
                      style={{ marginLeft: "0.5rem" }}
                    >
                      Devolver
                    </button>
                  )}
                </li>
              ))}
          </ul>
        )}
      </section>
    </div>
  );
}
