// src/components/BookForm.js
import React, { useState, useEffect } from "react";
import "../assets/styles.css";
import { getAuthHeaders, refreshToken, logout } from "../services/api";

function formatErrorData(data) {
  if (!data) return null;
  if (typeof data === "string") return data;
  if (Array.isArray(data)) return data.join(" | ");
  if (typeof data === "object") {
    return Object.entries(data)
      .map(([k, v]) => (Array.isArray(v) ? `${k}: ${v.join(" ; ")}` : `${k}: ${v}`))
      .join(" ‚∏∫ ");
  }
  return String(data);
}

export default function BookForm({ refreshBooks }) {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [available, setAvailable] = useState(true);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ text: "", type: "" });
  const [books, setBooks] = useState([]);

  // üîπ Busca livros existentes
  const fetchBooks = async () => {
    setLoading(true);
    try {
      const res = await fetch("http://127.0.0.1:8000/api/books/", {
        headers: getAuthHeaders(),
      });

      if (res.status === 401) {
        const newToken = await refreshToken();
        if (newToken) return fetchBooks();
        logout();
        alert("‚ö†Ô∏è Sess√£o expirada. Fa√ßa login novamente.");
        return;
      }

      if (!res.ok) throw new Error("Erro ao carregar livros.");
      const data = await res.json();
      setBooks(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
      setMessage({ text: "‚ùå Erro ao carregar livros.", type: "error" });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  // üîπ Cria novo livro
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ text: "", type: "" });

    try {
      const res = await fetch("http://127.0.0.1:8000/api/books/", {
        method: "POST",
        headers: {
          ...getAuthHeaders(),
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ title, author, available }),
      });

      if (res.status === 401) {
        const newToken = await refreshToken();
        if (newToken) return handleSubmit(e);
        logout();
        alert("‚ö†Ô∏è Sess√£o expirada. Fa√ßa login novamente.");
        return;
      }

      const data = await res.json().catch(() => ({}));

      if (res.ok) {
        setMessage({ text: "‚úÖ Livro criado com sucesso!", type: "success" });
        setTitle("");
        setAuthor("");
        setAvailable(true);
        fetchBooks();
        refreshBooks?.();
      } else {
        const msg = formatErrorData(data) || "Erro desconhecido.";
        setMessage({ text: `‚ùå Erro ao criar livro ‚Äî ${msg}`, type: "error" });
      }
    } catch (err) {
      console.error(err);
      setMessage({
        text: "‚ùå Erro inesperado. Tente novamente mais tarde.",
        type: "error",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="book-form-container">
      <form
        onSubmit={handleSubmit}
        className="book-form"
        aria-label="Formul√°rio de cadastro de livros"
      >
        <h3 tabIndex="0">üìò Cadastro de Livro</h3>

        {message.text && (
          <p
            role="status"
            aria-live="polite"
            tabIndex="0"
            className={`form-message ${message.type}`}
          >
            {message.text}
          </p>
        )}

        <label htmlFor="title">T√≠tulo:</label>
        <input
          id="title"
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <label htmlFor="author">Autor:</label>
        <input
          id="author"
          type="text"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
          required
        />

        <label htmlFor="available">
          <input
            id="available"
            type="checkbox"
            checked={available}
            onChange={(e) => setAvailable(e.target.checked)}
          />{" "}
          Dispon√≠vel
        </label>

        <button type="submit" disabled={loading}>
          {loading ? "üîÑ Processando..." : "‚ûï Criar Livro"}
        </button>
      </form>

      <section className="book-list" aria-label="Lista de livros">
        <h4 tabIndex="0">üìÑ Livros Cadastrados</h4>
        {books.length === 0 ? (
          <p tabIndex="0">Nenhum livro cadastrado.</p>
        ) : (
          <ul>
            {books.map((b) => (
              <li key={b.id} tabIndex="0">
                {b.title} ‚Äî {b.author}{" "}
                {b.available ? "(Dispon√≠vel)" : "(Indispon√≠vel)"}
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
}
