import React, { useState, useEffect } from "react";
import "../assets/styles.css";
import { getCurrentUser, getUsers, createUser, deleteUser, resetPassword } from "../services/ApiService";

// üîß Fun√ß√£o auxiliar para formatar mensagens de erro
function formatErrorData(data) {
  if (!data) return null;
  if (typeof data === "string") return data;
  if (Array.isArray(data)) return data.join(" | ");
  if (typeof data === "object") {
    return Object.entries(data)
      .map(([k, v]) =>
        Array.isArray(v) ? `${k}: ${v.join(" ; ")}` : `${k}: ${v}`
      )
      .join(" ‚∏∫ ");
  }
  return String(data);
}

export default function UserAdminPanel() {
  const [currentUser, setCurrentUser] = useState(null);
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ text: "", type: "" });

  // üü¢ Carregar usu√°rio atual e lista de usu√°rios
  useEffect(() => {
    async function loadData() {
      try {
        const user = await getCurrentUser();
        setCurrentUser(user);
        if (user?.is_superuser || user?.is_staff) {
          await fetchUsers();
        }
      } catch (error) {
        console.error("Erro ao carregar dados:", error);
      }
    }
    loadData();
  }, []);

  // üìã Buscar lista de usu√°rios
  async function fetchUsers() {
    try {
      const data = await getUsers();
      setUsers(data);
      setFilteredUsers(data);
    } catch (err) {
      console.error(err);
      setMessage({
        text: "‚ùå Erro ao carregar usu√°rios. Verifique permiss√µes.",
        type: "error",
      });
    }
  }

  const isAdmin = currentUser?.is_superuser || currentUser?.is_staff;
  if (!isAdmin) {
    return (
      <div role="alert" tabIndex="0" style={{ textAlign: "center", padding: "2rem", color: "#b00", fontWeight: "bold" }}>
        ‚ö†Ô∏è Acesso negado. Apenas administradores ou superusu√°rios podem acessar.
      </div>
    );
  }

  // üß© Criar novo usu√°rio
  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    setMessage({ text: "", type: "" });

    if (password !== password2) {
      setMessage({ text: "‚ö†Ô∏è As senhas n√£o coincidem.", type: "error" });
      setLoading(false);
      return;
    }

    try {
      const data = await createUser({ username, email, password });
      setMessage({ text: "‚úÖ Usu√°rio criado com sucesso!", type: "success" });
      setUsername(""); setEmail(""); setPassword(""); setPassword2("");
      fetchUsers();
    } catch (err) {
      const msg = formatErrorData(err?.response?.data || err.message) || "Erro desconhecido ao criar usu√°rio.";
      setMessage({ text: `‚ùå ${msg}`, type: "error" });
    } finally {
      setLoading(false);
    }
  }

  // ‚ùå Excluir usu√°rio
  async function handleDelete(userId) {
    if (!window.confirm("Tem certeza que deseja excluir este usu√°rio?")) return;
    setLoading(true);
    try {
      await deleteUser(userId);
      setUsers(prev => prev.filter(u => u.id !== userId));
      setFilteredUsers(prev => prev.filter(u => u.id !== userId));
      setMessage({ text: "üóëÔ∏è Usu√°rio exclu√≠do com sucesso!", type: "success" });
    } catch (err) {
      console.error(err);
      setMessage({ text: "‚ùå Erro ao excluir usu√°rio.", type: "error" });
    } finally {
      setLoading(false);
    }
  }

  // üîë Redefinir senha
  async function handleResetPassword(userId) {
    const novaSenha = Math.random().toString(36).slice(-8);
    try {
      await resetPassword(userId, novaSenha);
      alert(`üîë Nova senha tempor√°ria: ${novaSenha}`);
      setMessage({ text: `‚úÖ Senha redefinida para o usu√°rio ID ${userId}.`, type: "success" });
    } catch (err) {
      console.error(err);
      setMessage({ text: "‚ùå Erro ao redefinir senha.", type: "error" });
    }
  }

  // üîç Filtrar usu√°rios
  function handleSearch(e) {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);
    setFilteredUsers(users.filter(
      u => u.username.toLowerCase().includes(term) ||
           (u.email && u.email.toLowerCase().includes(term))
    ));
  }

  return (
    <div className="admin-panel" style={{ maxWidth: "950px", margin: "2rem auto", background: "#fff", padding: "1.5rem", borderRadius: "12px", boxShadow: "0 2px 6px rgba(0,0,0,0.1)" }}>
      <h2 style={{ textAlign: "center", color: "#004999" }}>‚öôÔ∏è Painel Administrativo de Usu√°rios</h2>
      {message.text && <p className={`form-message ${message.type}`} tabIndex="0" style={{ textAlign: "center", marginTop: "0.5rem", color: message.type === "success" ? "green" : "red", fontWeight: "bold" }}>{message.text}</p>}

      {/* Formul√°rio criar usu√°rio */}
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "0.5rem", marginBottom: "1.5rem" }}>
        <h4>üë§ Criar Novo Usu√°rio</h4>
        <input type="text" placeholder="Nome de usu√°rio" value={username} onChange={e => setUsername(e.target.value)} required />
        <input type="email" placeholder="E-mail" value={email} onChange={e => setEmail(e.target.value)} required />
        <input type="password" placeholder="Senha" value={password} onChange={e => setPassword(e.target.value)} required />
        <input type="password" placeholder="Confirmar senha" value={password2} onChange={e => setPassword2(e.target.value)} required />
        <button type="submit" disabled={loading}>{loading ? "Criando..." : "Criar Usu√°rio"}</button>
      </form>

      {/* Lista de usu√°rios */}
      <div>
        <h4>üìã Usu√°rios Cadastrados</h4>
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <input type="text" placeholder="üîç Buscar por nome ou e-mail..." value={searchTerm} onChange={handleSearch} style={{ flex: 1, marginRight: "10px", padding: "6px", borderRadius: "6px", border: "1px solid #ccc" }} />
          <button onClick={fetchUsers} disabled={loading}>üîÑ Atualizar</button>
        </div>
        <p style={{ textAlign: "right", color: "#555", fontSize: "0.9rem" }}>{filteredUsers.length} usu√°rio(s) encontrado(s)</p>

        <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "10px" }}>
          <thead>
            <tr style={{ background: "#004999", color: "#fff" }}>
              <th>ID</th>
              <th>Usu√°rio</th>
              <th>E-mail</th>
              <th>A√ß√µes</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.length > 0 ? filteredUsers.map(u => (
              <tr key={u.id}>
                <td>{u.id}</td>
                <td>{u.username}</td>
                <td>{u.email}</td>
                <td>
                  <button onClick={() => handleResetPassword(u.id)} style={{ marginRight: "8px" }}>üîë Redefinir Senha</button>
                  <button onClick={() => handleDelete(u.id)} style={{ backgroundColor: "#d9534f", color: "#fff", borderRadius: "4px" }}>‚ùå Excluir</button>
                </td>
              </tr>
            )) : <tr><td colSpan="4" style={{ textAlign: "center" }}>Nenhum usu√°rio encontrado.</td></tr>}
          </tbody>
        </table>
      </div>
    </div>
  );
}
