// src/components/LoanComponents.js
import React, { useEffect, useState } from "react";
import {
  getBooks,
  getUsers,
  getLoans,
  createLoan,
  returnBook,
} from "../Api";

export default function LoanComponents() {
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loans, setLoans] = useState([]);

  // inputs
  const [selectedUser, setSelectedUser] = useState("");
  const [typedUser, setTypedUser] = useState("");

  const [selectedBook, setSelectedBook] = useState("");
  const [typedBook, setTypedBook] = useState("");

  const [turma, setTurma] = useState("");

  // carregar dados
  const loadData = async () => {
    const b = await getBooks();
    setBooks(Array.isArray(b) ? b : b.results || []);

    const u = await getUsers();
    setUsers(Array.isArray(u) ? u : u.results || []);

    const l = await getLoans();
    setLoans(Array.isArray(l) ? l : l.results || []);
  };

  useEffect(() => {
    loadData();
  }, []);

  // registrar empr√©stimo
  const handleCreateLoan = async () => {
    const userToSend =
      typedUser.trim() !== "" ? typedUser.trim() : selectedUser || null;

    const bookToSend =
      typedBook.trim() !== "" ? typedBook.trim() : selectedBook || null;

    if (!bookToSend) {
      alert("‚ö†Ô∏è Selecione ou digite um livro!");
      return;
    }

    const payload = {
      book_id: bookToSend,
      user_id: userToSend || undefined,
      turma: turma || "",
    };

    try {
      const ok = await createLoan(payload);

      if (ok) {
        alert("üìö Empr√©stimo registrado com sucesso!");
        loadData();

        setSelectedUser("");
        setTypedUser("");

        setSelectedBook("");
        setTypedBook("");

        setTurma("");
      }
    } catch (err) {
      console.error("Erro ao registrar empr√©stimo:", err);
      alert("‚ùå Erro ao registrar empr√©stimo.");
    }
  };

  // devolver livro
  const handleReturn = async (loanId) => {
    if (!window.confirm("Confirmar devolu√ß√£o?")) return;

    try {
      const ok = await returnBook(loanId);

      if (ok) {
        alert("üì¶ Livro devolvido com sucesso!");
        loadData();
      }
    } catch (err) {
      alert("‚ùå Erro ao registrar devolu√ß√£o.");
    }
  };

  return (
    <div className="loan-panel">
      <h2>üìö Gerenciar Empr√©stimos</h2>

      {/* USER INPUT */}
      <div className="loan-field">
        <label>üë§ Usu√°rio:</label>

        <input
          type="text"
          value={typedUser}
          placeholder="Digite o nome ou ID do usu√°rio..."
          onChange={(e) => {
            setTypedUser(e.target.value);
            setSelectedUser("");
          }}
        />

        <select
          value={selectedUser}
          onChange={(e) => {
            setSelectedUser(e.target.value);
            setTypedUser("");
          }}
        >
          <option value="">Selecionar usu√°rio‚Ä¶</option>
          {users.map((u) => (
            <option key={u.id} value={u.id}>
              {u.username} ({u.email})
            </option>
          ))}
        </select>
      </div>

      {/* BOOK INPUT */}
      <div className="loan-field">
        <label>üìò Livro:</label>

        <input
          type="text"
          value={typedBook}
          placeholder="Digite o ID ou nome do livro‚Ä¶"
          onChange={(e) => {
            setTypedBook(e.target.value);
            setSelectedBook("");
          }}
        />

        <select
          value={selectedBook}
          onChange={(e) => {
            setSelectedBook(e.target.value);
            setTypedBook("");
          }}
        >
          <option value="">Selecionar livro‚Ä¶</option>
          {books.map((b) => (
            <option key={b.id} value={b.id}>
              {b.title} ‚Äî {b.author}
            </option>
          ))}
        </select>
      </div>

      {/* TURMA */}
      <div className="loan-field">
        <label>üè´ Turma (opcional):</label>
        <input
          type="text"
          value={turma}
          onChange={(e) => setTurma(e.target.value)}
          placeholder="Ex: 8¬∫ Ano A"
        />
      </div>

      <button className="btn-primary" onClick={handleCreateLoan}>
        ‚ûï Registrar Empr√©stimo
      </button>

      <hr />

      {/* LISTA DE EMPR√âSTIMOS */}
      <h3>üìÑ Empr√©stimos Registrados</h3>

      <ul className="loan-list">
        {loans.map((loan) => (
          <li key={loan.id}>
            <strong>{loan.book_title}</strong> ‚Äî {loan.user_username || "Sem usu√°rio"}
            <br />
            üìÖ Retirada: {new Date(loan.borrowed_at).toLocaleDateString()}
            {loan.turma && <div>üè´ Turma: {loan.turma}</div>}
            {loan.returned ? (
              <span style={{ color: "green" }}>‚úî Devolvido</span>
            ) : (
              <button onClick={() => handleReturn(loan.id)}>‚Ü© Registrar Devolu√ß√£o</button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
