// src/components/LoanButton.js
import React, { useState } from "react";
import PropTypes from "prop-types";
import { refreshToken, logout, getAuthHeaders } from "../services/api";

export default function LoanButton({ bookId, refreshBooks, disabled }) {
  const [loading, setLoading] = useState(false);

  const requestLoan = async () => {
    const token = localStorage.getItem("access_token");
    if (!token) {
      alert("‚ö†Ô∏è Voc√™ precisa estar logado para solicitar um empr√©stimo!");
      return;
    }

    setLoading(true);

    try {
      const res = await fetch("http://127.0.0.1:8000/api/loans/", {
        method: "POST",
        headers: {
          ...getAuthHeaders(),
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ book: bookId }),
      });

      // üîπ Se token expirou, tenta atualizar e repetir
      if (res.status === 401) {
        const newToken = await refreshToken();
        if (newToken) {
          return requestLoan(); // tenta novamente
        } else {
          logout();
          alert("‚ö†Ô∏è Sess√£o expirada. Fa√ßa login novamente.");
          return;
        }
      }

      // üîπ Sucesso
      if (res.ok) {
        alert("‚úÖ Empr√©stimo solicitado com sucesso!");
        if (refreshBooks) refreshBooks();
        return;
      }

      // üîπ Erro gen√©rico
      const errData = await res.json().catch(() => ({}));
      alert(
        "‚ö†Ô∏è Erro ao solicitar empr√©stimo: " +
          (errData.detail || "N√£o foi poss√≠vel completar a opera√ß√£o.")
      );
    } catch (error) {
      console.error("Erro ao solicitar empr√©stimo:", error);
      alert("‚ùå Ocorreu um erro inesperado. Tente novamente mais tarde.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <button
      onClick={requestLoan}
      disabled={loading || disabled}
      aria-label="Solicitar empr√©stimo de livro"
      aria-live="polite"
      className={`loan-btn ${loading ? "loading" : ""}`}
      style={{
        padding: "0.5rem 1rem",
        borderRadius: "6px",
        border: "none",
        cursor: loading || disabled ? "not-allowed" : "pointer",
        backgroundColor: loading || disabled ? "#999" : "#007bff",
        color: "#fff",
        fontWeight: "bold",
        transition: "background 0.3s",
      }}
    >
      {loading ? "üîÑ Solicitando empr√©stimo..." : "üìñ Solicitar Empr√©stimo"}
    </button>
  );
}

LoanButton.propTypes = {
  bookId: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
  refreshBooks: PropTypes.func.isRequired,
  disabled: PropTypes.bool,
};

LoanButton.defaultProps = {
  disabled: false,
};
