// src/components/BookList.js
import React, { useEffect, useState } from "react";
import { getBooks } from "../services/api";
import BookGrid from "./BookGrid";
import "./booklist.css";

export default function BookList({ refreshBooksParent = null, isLoggedIn = false }) {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // üîπ Busca livros do backend ‚Äî s√≥ se logado
  const fetchBooks = async () => {
    if (!isLoggedIn) return; // impede acesso antes do login

    setLoading(true);
    setError("");

    try {
      let token = null;
      try {
        token = localStorage.getItem("access_token");
      } catch {
        console.warn("‚ö†Ô∏è Acesso ao localStorage bloqueado.");
      }

      if (!token) {
        setError("Voc√™ precisa estar logado para ver os livros.");
        setLoading(false);
        return;
      }

      const booksData = await getBooks();
      const list = Array.isArray(booksData)
        ? booksData
        : booksData?.results || [];

      setBooks(list);

      if (refreshBooksParent) refreshBooksParent(list);

      console.log("üìö Dados recebidos do backend:", list);
    } catch (err) {
      console.error("Erro ao buscar livros:", err);
      setError(
        "‚ùå N√£o foi poss√≠vel carregar os livros. Verifique o servidor ou o token."
      );
    } finally {
      setLoading(false);
    }
  };

  // üîπ Atualiza livros quando login muda
  useEffect(() => {
    if (isLoggedIn) {
      fetchBooks();
    } else {
      setBooks([]); // limpa livros se deslogar
    }
  }, [isLoggedIn]);

  // üîπ Fun√ß√£o de leitura em voz alta (segura)
  const speakBook = (book) => {
    if (!book) return;
    if ("speechSynthesis" in window) {
      const msg = new SpeechSynthesisUtterance(
        `Livro: ${book.title || "n√£o informado"}. ` +
        `Autor: ${book.author || "n√£o informado"}. ` +
        `Ano: ${book.publication_year || "n√£o informado"}. ` +
        `${book.genre ? `G√™nero: ${book.genre}. ` : ""}` +
        `Status: ${book.available ? "Dispon√≠vel" : "Emprestado"}.`
      );
      window.speechSynthesis.speak(msg);
    } else {
      console.warn("Seu navegador n√£o suporta leitura de √°udio.");
    }
  };

  return (
    <section className="book-list" aria-label="Cat√°logo de livros">
      {loading && (
        <p tabIndex="0" aria-live="polite">
          ‚è≥ Carregando livros...
        </p>
      )}

      {error && (
        <p tabIndex="0" aria-live="polite" className="error-message">
          {error}
        </p>
      )}

      {!loading && !error && books.length === 0 && isLoggedIn && (
        <p tabIndex="0" aria-live="polite">
          üì≠ Nenhum livro encontrado no cat√°logo.
        </p>
      )}

      {!loading && !error && !isLoggedIn && (
        <p tabIndex="0" aria-live="polite">
          üîí Fa√ßa login para acessar o cat√°logo de livros.
        </p>
      )}

      {!loading && !error && books.length > 0 && (
        <BookGrid
          books={books.map((b) => ({
            ...b,
            speak: () => speakBook(b),
            altText: `Capa do livro ${b.title || "sem t√≠tulo"}`,
          }))}
          refreshBooks={fetchBooks}
        />
      )}
    </section>
  );
}
