// src/ApiService.js
const API_URL = "http://127.0.0.1:8000/api/";

/* ===============================
   üîπ Autentica√ß√£o JWT
   =============================== */
export async function login(username, password) {
  const res = await fetch(`${API_URL}token/`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ username, password }),
  });

  if (!res.ok) throw new Error("‚ùå Falha no login ‚Äî verifique suas credenciais.");

  const data = await res.json();
  localStorage.setItem("access_token", data.access);
  localStorage.setItem("refresh_token", data.refresh);

  return data;
}

export async function refreshToken() {
  const refresh = localStorage.getItem("refresh_token");
  if (!refresh) return null;

  try {
    const res = await fetch(`${API_URL}token/refresh/`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ refresh }),
    });

    if (!res.ok) throw new Error("Erro ao atualizar token");

    const data = await res.json();
    localStorage.setItem("access_token", data.access);
    return data.access;
  } catch (err) {
    console.error("Erro ao renovar token:", err);
    logout();
    return null;
  }
}

export function getAuthHeaders() {
  const token = localStorage.getItem("access_token");
  return token
    ? { Authorization: `Bearer ${token}`, "Content-Type": "application/json" }
    : { "Content-Type": "application/json" };
}

export function logout() {
  localStorage.removeItem("access_token");
  localStorage.removeItem("refresh_token");
}

/* ===============================
   üîπ Fun√ß√£o fetch com refresh autom√°tico
   =============================== */
async function fetchWithAuth(url, options = {}, retry = true) {
  let headers = { ...getAuthHeaders(), ...options.headers };
  let res = await fetch(url, { ...options, headers });

  if (res.status === 401 && retry) {
    const newToken = await refreshToken();
    if (newToken) {
      headers = { ...headers, Authorization: `Bearer ${newToken}` };
      res = await fetch(url, { ...options, headers });
    }
  }

  return res;
}

/* ===============================
   üîπ Usu√°rios
   =============================== */
export async function getUsers() {
  const res = await fetchWithAuth(`${API_URL}users/`);
