import React, { useEffect, useState } from "react";
import "./assets/styles.css";
import VLibras from "@djpfs/react-vlibras";

import { useAuth } from "./context/AuthContext";
import { getBooks, getUsers, getLoans } from "./Api";
import { USER_ROLES } from "./constants/userRoles";

import BookList from "./components/BookList";
import AdminPanel from "./components/AdminPanel";
import LoginForm from "./components/LoginForm";

const Loader = () => <p>Carregando sistema...</p>;

export default function App() {
  const { user, role, loading, logout } = useAuth();
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [activeTab, setActiveTab] = useState("livros");

  const isSuperUser = role === USER_ROLES.SUPERUSER;

  // üîç Controles de zoom
  const setZoom = (size) => {
    document.body.classList.remove("zoom-small", "zoom-medium", "zoom-large");
    document.body.classList.add(size);
  };

  // üîπ Buscar livros
  async function fetchBooks() {
    try {
      const data = await getBooks();
      setBooks(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao carregar livros");
    }
  }

  // üîπ Buscar usu√°rios
  async function fetchUsers() {
    if (!isSuperUser) return;
    try {
      const data = await getUsers();
      setUsers(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao carregar usu√°rios");
    }
  }

  // üîπ Buscar empr√©stimos
  async function fetchLoans() {
    if (!isSuperUser) return;
    try {
      await getLoans();
    } catch (err) {
      console.error("Erro ao carregar empr√©stimos");
    }
  }

  // üîπ Carregar dados quando usu√°rio loga
  useEffect(() => {
    if (user) {
      fetchBooks();
      if (isSuperUser) {
        fetchUsers();
        fetchLoans();
      }
    }
  }, [user, role]);

  if (loading) return <Loader />;

  return (
    <div className="app-container">
      {/* üîπ Cabe√ßalho */}
      <header className="app-header">
        <h1>üìö Sala de Leitura Online Acess√≠vel</h1>

        {user ? (
          <div className="user-info">
            <p>
              Bem-vindo, <strong>{user.username}</strong> (
              {isSuperUser
                ? "Superusu√°rio"
                : role === USER_ROLES.ADMIN
                ? "Administrador"
                : "Usu√°rio"}
              )
            </p>
            <button onClick={logout}>Sair</button>
          </div>
        ) : (
          <LoginForm />
        )}

        {/* üîπ Acessibilidade: Zoom + Contraste */}
        {user && (
          <div
            className="accessibility-controls"
            style={{
              marginTop: "1rem",
              textAlign: "center",
              display: "flex",
              justifyContent: "center",
              gap: "0.5rem",
              flexWrap: "wrap",
            }}
          >
            <button onClick={() => setZoom("zoom-small")}>üîç-</button>
            <button onClick={() => setZoom("zoom-medium")}>üîç</button>
            <button onClick={() => setZoom("zoom-large")}>üîç+</button>

            <button
              onClick={() =>
                document
                  .querySelector(".app-container")
                  .classList.toggle("dark-mode")
              }
            >
              üé® Contraste
            </button>
          </div>
        )}
      </header>

      {/* üîπ Navega√ß√£o por abas */}
      {user && (
        <div className="tab-navigation">
          <button
            className={activeTab === "livros" ? "active-tab" : ""}
            onClick={() => setActiveTab("livros")}
          >
            üìö Livros
          </button>

          {isSuperUser && (
            <button
              className={activeTab === "admin" ? "active-tab" : ""}
              onClick={() => setActiveTab("admin")}
            >
              ‚öôÔ∏è Administra√ß√£o
            </button>
          )}
        </div>
      )}

      {/* üîπ Conte√∫do principal */}
      <main>
        {activeTab === "livros" && (
          <BookList books={books} refreshBooks={fetchBooks} />
        )}

        {activeTab === "admin" && isSuperUser && (
          <AdminPanel
            role={role}
            books={books}
            users={users}
            fetchBooks={fetchBooks}
            fetchUsers={fetchUsers}
            fetchLoans={fetchLoans}
          />
        )}
      </main>

      {/* üîπ Rodap√© */}
      <footer className="app-footer">
        <p>
          Desenvolvido com acessibilidade total ‚Äî Libras, teclado, contraste e
          zoom.
        </p>
      </footer>

      {/* üü¶ VLibras carregado corretamente */}
      <VLibras />
    </div>
  );
}
