import React, { useEffect, useState } from "react";
import api from "../services/api";
import Navbar from "../components/Navbar";
import BookCard from "../components/BookCard";
import LoanList from "../components/LoanList";

const UserDashboard = () => {
  const [books, setBooks] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchBooks = async () => {
    try {
      setLoading(true);
      setError("");
      const res = await api.get("/books/", {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });

      // ‚úÖ Ajuste para lidar com API paginada ou lista simples
      const lista = Array.isArray(res.data) ? res.data : res.data.results || [];
      setBooks(lista);
      console.log("üìö Livros carregados:", lista);
    } catch (err) {
      console.error("‚ùå Erro ao buscar livros:", err);
      setError("N√£o foi poss√≠vel carregar os livros. Tente novamente.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  const filteredBooks = books.filter(
    (b) =>
      b.title.toLowerCase().includes(search.toLowerCase()) ||
      (b.author && b.author.toLowerCase().includes(search.toLowerCase()))
  );

  return (
    <div style={{ padding: "20px" }}>
      <Navbar />

      {/* Se√ß√£o de Acervo */}
      <section style={{ marginBottom: "40px" }}>
        <h2
          style={{
            color: "#004999",
            textAlign: "center",
            marginBottom: "20px",
          }}
          tabIndex="0"
        >
          üìö Acervo de Livros
        </h2>

        {/* Barra de busca */}
        <div style={{ textAlign: "center", marginBottom: "20px" }}>
          <input
            type="text"
            placeholder="Pesquisar por t√≠tulo ou autor..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            style={{
              width: "60%",
              padding: "10px",
              borderRadius: "8px",
              border: "1px solid #ccc",
              fontSize: "16px",
            }}
            aria-label="Campo de pesquisa por t√≠tulo ou autor"
          />
        </div>

        {/* Estado de carregamento ou erro */}
        {loading && (
          <p style={{ textAlign: "center" }} aria-live="polite">
            ‚è≥ Carregando livros...
          </p>
        )}
        {error && (
          <p style={{ textAlign: "center", color: "red" }} role="alert">
            {error}
          </p>
        )}

        {/* Grid de livros */}
        {!loading && !error && (
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
              gap: "20px",
            }}
            role="list"
            aria-label="Lista de livros dispon√≠veis"
          >
            {filteredBooks.length > 0 ? (
              filteredBooks.map((book) => (
                <BookCard key={book.id} book={book} />
              ))
            ) : (
              <p
                style={{ textAlign: "center", gridColumn: "1 / -1" }}
                role="status"
              >
                üì≠ Nenhum livro encontrado.
              </p>
            )}
          </div>
        )}
      </section>

      {/* Se√ß√£o de Meus Empr√©stimos */}
      <section>
        <h2
          style={{
            color: "#004999",
            textAlign: "center",
            marginBottom: "20px",
          }}
          tabIndex="0"
        >
          üìñ Meus Empr√©stimos
        </h2>
        <LoanList />
      </section>
    </div>
  );
};

export default UserDashboard;
