import React, { useEffect, useState } from "react";
import "./assets/styles.css";
import VLibras from "@djpfs/react-vlibras";

import { useAuth } from "./context/AuthContext";
import { getBooks, getUsers, getLoans } from "./Api";
import { USER_ROLES } from "./constants/userRoles";

import BookList from "./components/BookList";
import AdminPanel from "./components/AdminPanel";
import LoginForm from "./components/LoginForm";

const Loader = () => <p>Carregando sistema...</p>;

export default function App() {
  const { user, role, loading, logout } = useAuth();
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [activeTab, setActiveTab] = useState("livros");

  const isSuperUser = role === USER_ROLES.SUPERUSER;

  async function fetchBooks() {
    try {
      const data = await getBooks();
      setBooks(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao carregar livros");
    }
  }

  async function fetchUsers() {
    if (!isSuperUser) return;
    try {
      const data = await getUsers();
      setUsers(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao carregar usu√°rios");
    }
  }

  async function fetchLoans() {
    if (!isSuperUser) return;
    try {
      await getLoans();
    } catch (err) {
      console.error("Erro ao carregar empr√©stimos");
    }
  }

  useEffect(() => {
    if (user) {
      fetchBooks();
      if (isSuperUser) {
        fetchUsers();
        fetchLoans();
      }
    }
  }, [user, role]);

  if (loading) return <Loader />;

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>üìö Biblioteca Online Acess√≠vel</h1>

        {user ? (
          <div className="user-info">
            <p>
              Bem-vindo, <strong>{user.username}</strong> (
              {isSuperUser
                ? "Superusu√°rio"
                : role === USER_ROLES.ADMIN
                ? "Administrador"
                : "Usu√°rio"}
              )
            </p>
            <button onClick={logout}>Sair</button>
          </div>
        ) : (
          <LoginForm />
        )}
      </header>

      {user && (
        <div className="tab-navigation">
          <button
            className={activeTab === "livros" ? "active-tab" : ""}
            onClick={() => setActiveTab("livros")}
          >
            üìö Livros
          </button>

          {isSuperUser && (
            <button
              className={activeTab === "admin" ? "active-tab" : ""}
              onClick={() => setActiveTab("admin")}
            >
              ‚öôÔ∏è Administra√ß√£o
            </button>
          )}
        </div>
      )}

      <main>
        {activeTab === "livros" && (
          <BookList books={books} refreshBooks={fetchBooks} />
        )}

        {activeTab === "admin" && isSuperUser && (
          <AdminPanel
            role={role}
            books={books}
            users={users}
            fetchBooks={fetchBooks}
            fetchUsers={fetchUsers}
            fetchLoans={fetchLoans}
          />
        )}
      </main>

      <footer className="app-footer">
        <p>
          Desenvolvido com acessibilidade total ‚Äî suportando Libras, teclado e contraste.
        </p>
      </footer>

      {typeof window !== "undefined" && <VLibras forceOnload />}
    </div>
  );
}
