// src/components/LoanComponents.js

import React, { useEffect, useState } from "react";
import {
  getLoans,
  getBooks,
  getUsers,
  createLoan,
  returnBook,
} from "../Api";
import "../assets/styles.css";

export default function LoanComponents() {
  const [loans, setLoans] = useState([]);
  const [users, setUsers] = useState([]);
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);

  // Campos do formul√°rio
  const [selectedUser, setSelectedUser] = useState("");
  const [typedUser, setTypedUser] = useState("");

  const [selectedBook, setSelectedBook] = useState("");
  const [typedBook, setTypedBook] = useState("");

  // ==============================
  // CARREGA DADOS DO SERVIDOR
  // ==============================
  const loadData = async () => {
    setLoading(true);

    try {
      const loanData = await getLoans();
      const bookData = await getBooks();
      const userData = await getUsers();

      // CONVERTE SEMPRE PARA ARRAY
      setLoans(
        Array.isArray(loanData) ? loanData : loanData?.results ?? []
      );

      setBooks(
        Array.isArray(bookData) ? bookData : bookData?.results ?? []
      );

      setUsers(
        Array.isArray(userData)
          ? userData
          : Array.isArray(userData?.results)
          ? userData.results
          : []
      );
    } catch (err) {
      console.error("Erro ao carregar dados:", err);
    }

    setLoading(false);
  };

  useEffect(() => {
    loadData();
  }, []);

  // ==============================
  // REGISTRAR EMPR√âSTIMO
  // ==============================
  const handleCreateLoan = async () => {
    const userToSend =
      typedUser.trim() !== "" ? typedUser : selectedUser;

    const bookToSend =
      typedBook.trim() !== "" ? typedBook : selectedBook;

    if (!userToSend || !bookToSend) {
      alert("‚ö†Ô∏è Preencha usu√°rio e livro!");
      return;
    }

    const payload = {
      user: userToSend,
      book: bookToSend,
    };

    const ok = await createLoan(payload);

    if (ok) {
      alert("üìö Empr√©stimo registrado com sucesso!");
      loadData();
      setSelectedUser("");
      setTypedUser("");
      setSelectedBook("");
      setTypedBook("");
    }
  };

  // ==============================
  // REGISTRAR DEVOLU√á√ÉO
  // ==============================
  const handleReturn = async (loanId) => {
    const ok = await returnBook(loanId);

    if (ok) {
      alert("üìï Livro devolvido com sucesso!");
      loadData();
    }
  };

  // ========================================================
  // ================== RENDERIZA√á√ÉO ========================
  // ========================================================

  return (
    <section className="loan-panel">
      <h2>üìö Gest√£o de Empr√©stimos</h2>

      {loading && <p>‚è≥ Carregando dados...</p>}

      <div className="loan-form">
        <h3>‚ûï Registrar Empr√©stimo</h3>

        {/* =========================
            Selecionar OU digitar usu√°rio
        ========================== */}
        <label>Usu√°rio:</label>
        <select
          value={selectedUser}
          onChange={(e) => setSelectedUser(e.target.value)}
        >
          <option value="">Selecione um usu√°rio</option>
          {users.map((u) => (
            <option key={u.id} value={u.id}>
              {u.username}
            </option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Ou digite o nome do usu√°rio"
          value={typedUser}
          onChange={(e) => setTypedUser(e.target.value)}
        />

        {/* =========================
            Selecionar OU digitar livro
        ========================== */}
        <label>Livro:</label>
        <select
          value={selectedBook}
          onChange={(e) => setSelectedBook(e.target.value)}
        >
          <option value="">Selecione um livro</option>
          {books.map((b) => (
            <option key={b.id} value={b.id}>
              {b.title}
            </option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Ou digite o ID/t√≠tulo do livro"
          value={typedBook}
          onChange={(e) => setTypedBook(e.target.value)}
        />

        <button className="btn-primary" onClick={handleCreateLoan}>
          üìñ Registrar Empr√©stimo
        </button>
      </div>

      {/* ===============================
          LISTA DE EMPR√âSTIMOS EXISTENTES
      ================================ */}
      <h3 style={{ marginTop: "2rem" }}>üìÑ Empr√©stimos Ativos</h3>

      {loans.length === 0 ? (
        <p>Nenhum empr√©stimo registrado.</p>
      ) : (
        <table className="loan-table">
          <thead>
            <tr>
              <th>Usu√°rio</th>
              <th>Livro</th>
              <th>Data</th>
              <th>Devolver</th>
            </tr>
          </thead>
          <tbody>
            {loans.map((loan) => (
              <tr key={loan.id}>
                <td>{loan.user_username || loan.username}</td>
                <td>{loan.book_title}</td>
                <td>{loan.loan_date}</td>
                <td>
                  <button
                    className="btn-return"
                    onClick={() => handleReturn(loan.id)}
                  >
                    üîô Devolver
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </section>
  );
}
