// src/components/LoanComponents.js
import React, { useEffect, useState } from "react";
import { getAuthHeaders, refreshToken, logout } from "../Api";

const API_URL = "http://127.0.0.1:8000/api/";

export default function LoanComponents({ role, user }) {
  const [loans, setLoans] = useState([]);
  const [users, setUsers] = useState([]);
  const [books, setBooks] = useState([]);
  const [selectedUser, setSelectedUser] = useState("");
  const [selectedBook, setSelectedBook] = useState("");
  const [loading, setLoading] = useState(true);

  // üìå Carrega todos os dados conforme o tipo de usu√°rio
  useEffect(() => {
    fetchInitialData();
  }, [role]);

  const fetchInitialData = async () => {
    setLoading(true);
    try {
      if (role === "superuser" || role === "admin") {
        await fetchAllLoans();
        await fetchUsers();
        await fetchAvailableBooks();
      } else {
        await fetchMyLoans();
      }
    } catch (err) {
      console.error("Erro ao carregar dados de empr√©stimo:", err);
    } finally {
      setLoading(false);
    }
  };

  // üîπ Carregar empr√©stimos (admin)
  const fetchAllLoans = async () => {
    const response = await fetch(`${API_URL}loans/`, {
      headers: getAuthHeaders(),
    });

    const data = await response.json();
    setLoans(data.results || data || []);
  };

  // üîπ Carregar empr√©stimos do usu√°rio comum
  const fetchMyLoans = async () => {
    const response = await fetch(`${API_URL}loans/?me=true`, {
      headers: getAuthHeaders(),
    });

    const data = await response.json();
    setLoans(data.results || data || []);
  };

  // üîπ Buscar usu√°rios
  const fetchUsers = async () => {
    const response = await fetch(`${API_URL}users/`, {
      headers: getAuthHeaders(),
    });

    const data = await response.json();
    setUsers(data.results || data || []);
  };

  // üîπ Buscar livros dispon√≠veis
  const fetchAvailableBooks = async () => {
    const response = await fetch(`${API_URL}books/?available=true`, {
      headers: getAuthHeaders(),
    });

    const data = await response.json();
    setBooks(data.results || data || []);
  };

  // üîπ Criar empr√©stimo (admin)
  const createLoan = async () => {
    if (!selectedUser || !selectedBook) {
      alert("Selecione um usu√°rio e um livro!");
      return;
    }

    const response = await fetch(`${API_URL}loans/`, {
      method: "POST",
      headers: {
        ...getAuthHeaders(),
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        user: selectedUser,
        book: selectedBook,
      }),
    });

    if (response.ok) {
      alert("‚úÖ Empr√©stimo registrado!");
      fetchInitialData();
    } else {
      alert("‚ùå Erro ao registrar empr√©stimo.");
    }
  };

  // üîπ Registrar devolu√ß√£o
  const returnLoan = async (loanId) => {
    const response = await fetch(`${API_URL}loans/${loanId}/return/`, {
      method: "PATCH",
      headers: getAuthHeaders(),
    });

    if (response.ok) {
      alert("üìó Livro devolvido!");
      fetchInitialData();
    } else {
      alert("‚ùå Erro ao registrar devolu√ß√£o.");
    }
  };

  if (loading) return <p>‚è≥ Carregando dados...</p>;

  return (
    <section style={{ padding: "1rem" }}>
      <h2>üìö Empr√©stimos</h2>

      {/* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */}
      {/* ADMIN / SUPERUSER ‚Üí Criar empr√©stimo */}
      {(role === "superuser" || role === "admin") && (
        <div
          style={{
            background: "#eef",
            padding: "1rem",
            borderRadius: "8px",
            marginBottom: "1rem",
          }}
        >
          <h3>‚ûï Registrar novo empr√©stimo</h3>

          <label>Usu√°rio:</label>
          <select
            value={selectedUser}
            onChange={(e) => setSelectedUser(e.target.value)}
          >
            <option value="">Selecione</option>
            {users.map((u) => (
              <option key={u.id} value={u.id}>
                {u.username}
              </option>
            ))}
          </select>

          <label style={{ marginTop: "1rem" }}>Livro dispon√≠vel:</label>
          <select
            value={selectedBook}
            onChange={(e) => setSelectedBook(e.target.value)}
          >
            <option value="">Selecione</option>
            {books.map((b) => (
              <option key={b.id} value={b.id}>
                {b.title}
              </option>
            ))}
          </select>

          <button onClick={createLoan} style={{ marginTop: "1rem" }}>
            üìñ Registrar Empr√©stimo
          </button>
        </div>
      )}

      {/* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */}
      {/* Lista de empr√©stimos */}
      <h3>
        {role === "superuser" || role === "admin"
          ? "üìÑ Todos os empr√©stimos"
          : "üìÑ Meus empr√©stimos"}
      </h3>

      {loans.length === 0 ? (
        <p>üì≠ Nenhum empr√©stimo encontrado.</p>
      ) : (
        <table
          style={{
            width: "100%",
            borderCollapse: "collapse",
            marginTop: "1rem",
          }}
        >
          <thead>
            <tr style={{ background: "#ddd" }}>
              {(role === "superuser" || role === "admin") && <th>Usu√°rio</th>}
              <th>Livro</th>
              <th>Data Empr√©stimo</th>
              <th>Data Devolu√ß√£o</th>
              {(role === "superuser" || role === "admin") && <th>A√ß√£o</th>}
            </tr>
          </thead>

          <tbody>
            {loans.map((loan) => (
              <tr key={loan.id}>
                {(role === "superuser" || role === "admin") && (
                  <td>{loan.user_username}</td>
                )}
                <td>{loan.book_title}</td>
                <td>{loan.loan_date}</td>
                <td>{loan.return_date || "‚Äî"}</td>

                {(role === "superuser" || role === "admin") && (
                  <td>
                    {!loan.return_date && (
                      <button
                        onClick={() => returnLoan(loan.id)}
                        style={{ background: "green", color: "white" }}
                      >
                        ‚úîÔ∏è Registrar Devolu√ß√£o
                      </button>
                    )}
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </section>
  );
}
