import React, { useState } from "react";
import BookForm from "./BookForm";
import LoanForm from "./LoanForm";
import ReturnForm from "./ReturnForm";
import UserForm from "./UserForm";
import BookList from "./BookList";
import "../assets/styles.css";

export default function AdminPanel({ role, books, users, fetchBooks, fetchUsers, fetchLoans }) {
  const [refreshKey, setRefreshKey] = useState(0);
  const handleRefresh = () => setRefreshKey((prev) => prev + 1);

  return (
    <main className="admin-panel" role="main" aria-label="Painel de Administra√ß√£o da Biblioteca">
      <h1 tabIndex="0">‚öôÔ∏è Painel de Administra√ß√£o</h1>

      {(role === "superuser" || role === "admin") && (
        <a
          href="http://127.0.0.1:8000/admin/"
          target="_blank"
          rel="noopener noreferrer"
          className="admin-link"
        >
          ‚öôÔ∏è Acessar Django Admin
        </a>
      )}

      <div className="admin-top-panels">
        <div className="admin-panel">
          <BookForm refreshBooks={fetchBooks} key={`book-${refreshKey}`} />
        </div>

        <div className="admin-panel">
          <UserForm refreshUsers={fetchUsers} key={`user-${refreshKey}`} />
        </div>

        <div className="admin-panel">
          <LoanForm onSave={fetchLoans} isAdmin={true} key={`loan-${refreshKey}`} />
        </div>
      </div>

      <div className="admin-book-list">
        <h3 tabIndex="0">üìö Livros Registrados</h3>
        <BookList books={books} refreshBooks={fetchBooks} key={`list-${refreshKey}`} />
      </div>

      <div className="admin-user-list">
        <h3 tabIndex="0">üë• Usu√°rios</h3>
        {users.length ? (
          <ul>
            {users.map((u) => (
              <li key={u.id}>
                {u.username} ({u.email}) {u.is_superuser && "‚≠ê Superusu√°rio"}
              </li>
            ))}
          </ul>
        ) : (
          <p>Nenhum usu√°rio registrado.</p>
        )}
      </div>
    </main>
  );
}
