// src/App.js
import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import VLibras from "@djpfs/react-vlibras";

import {
  getBooks,
  getUsers,
  getLoans,
  getCurrentUser,
  login,
  logout,
  refreshAccessToken,
} from "./services/auth"; // Ajuste para o path correto do auth

import BookList from "./components/BookList";
import BookForm from "./components/BookForm";
import UserForm from "./components/UserForm";
import LoanForm from "./components/LoanForm";
import AccessibilityBar from "./components/AccessibilityBar";
import AdminPanel from "./components/AdminPanel";

import { USER_ROLES } from "./constants/userRoles";
import "./App.css";

const Loader = () => <p>Carregando sistema...</p>;

// P√°gina inicial para usu√°rios comuns
const Home = ({ books, fetchBooks }) => (
  <section aria-label="Cat√°logo de livros">
    <h2 tabIndex="0">üìñ Cat√°logo de Livros</h2>
    <BookList books={books} refreshBooks={fetchBooks} />
  </section>
);

// Formul√°rio de login
function LoginForm({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setLoading(true);
    try {
      const data = await login(username, password);

      // Salva flags de permiss√£o no localStorage
      localStorage.setItem("username", username);
      localStorage.setItem("is_staff", data.is_staff);
      localStorage.setItem("is_superuser", data.is_superuser);

      await onLogin();
    } catch {
      setMessage("‚ùå Falha no login. Verifique usu√°rio e senha.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="login-form" aria-label="Login de usu√°rio">
      <h3 tabIndex="0">üîê Acesso ao Sistema</h3>
      {message && <p className="form-message error" role="alert">{message}</p>}
      <label htmlFor="username">Usu√°rio:</label>
      <input
        id="username"
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        required
      />
      <label htmlFor="password">Senha:</label>
      <input
        id="password"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      <button type="submit" disabled={loading}>
        {loading ? "Entrando..." : "Entrar"}
      </button>
    </form>
  );
}

export default function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(USER_ROLES.GUEST);
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loans, setLoans] = useState([]);
  const [loadingUser, setLoadingUser] = useState(true);

  const handleLogout = () => {
    logout();
    setUser(null);
    setRole(USER_ROLES.GUEST);
  };

  const fetchBooks = async () => {
    try {
      const data = await getBooks();
      setBooks(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao buscar livros:", err);
    }
  };

  const fetchUsers = async () => {
    if (![USER_ROLES.ADMIN, USER_ROLES.SUPERUSER].includes(role)) return;
    try {
      const data = await getUsers();
      setUsers(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao buscar usu√°rios:", err);
    }
  };

  const fetchLoans = async () => {
    try {
      const data = await getLoans();
      setLoans(Array.isArray(data) ? data : data.results || []);
    } catch (err) {
      console.error("Erro ao buscar empr√©stimos:", err);
    }
  };

  const loadUserData = async () => {
    setLoadingUser(true);

    const token = localStorage.getItem("access_token");
    if (!token) {
      setRole(USER_ROLES.GUEST);
      setLoadingUser(false);
      return;
    }

    try {
      const userData = await getCurrentUser();
      setUser(userData);

      // Define role com base em flags do backend ou localStorage
      const newRole = userData.is_superuser
        ? USER_ROLES.SUPERUSER
        : userData.is_staff
        ? USER_ROLES.ADMIN
        : USER_ROLES.USER;
      setRole(newRole);

      // Carrega dados necess√°rios
      await Promise.all([fetchBooks(), fetchLoans(), fetchUsers()]);
    } catch (err) {
      console.error("Erro ao carregar usu√°rio:", err);
      handleLogout();
    } finally {
      setLoadingUser(false);
    }
  };

  useEffect(() => {
    loadUserData();

    const interval = setInterval(async () => {
      const newToken = await refreshAccessToken();
      if (newToken) {
        await loadUserData();
      }
    }, 4 * 60 * 1000); // a cada 4 minutos

    return () => clearInterval(interval);
  }, []);

  if (loadingUser) return <Loader />;

  const isAdmin = [USER_ROLES.ADMIN, USER_ROLES.SUPERUSER].includes(role);

  return (
    <Router>
      <div className="app-container">
        <VLibras forceOnload />
        <AccessibilityBar />

        <header className="app-header">
          <h1 tabIndex="0">üìö Biblioteca Online Acess√≠vel</h1>
          {user ? (
            <div className="user-info">
              <p tabIndex="0">
                Bem-vindo, <strong>{user.username}</strong> (
                {role === USER_ROLES.SUPERUSER
                  ? "Superusu√°rio"
                  : role === USER_ROLES.ADMIN
                  ? "Administrador"
                  : "Usu√°rio"}
                )
              </p>
              <button onClick={handleLogout}>Sair</button>
            </div>
          ) : (
            <LoginForm onLogin={loadUserData} />
          )}
        </header>

        <main id="main-content" role="main">
          <Routes>
            <Route path="/" element={<Home books={books} fetchBooks={fetchBooks} />} />
            <Route
              path="/admin"
              element={
                isAdmin ? (
                  <AdminPanel
                    role={role}
                    books={books}
                    users={users}
                    fetchBooks={fetchBooks}
                    fetchUsers={fetchUsers}
                    fetchLoans={fetchLoans}
                  />
                ) : (
                  <Navigate to="/" />
                )
              }
            />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </main>

        <footer className="app-footer" role="contentinfo">
          <p tabIndex="0">
            Desenvolvido com acessibilidade total ‚Äî suporte a Libras, teclado e alto contraste.
          </p>
        </footer>
      </div>
    </Router>
  );
}
