import React, { useState, useEffect } from "react";
import { getBooks, getLoans, createLoan, returnBook } from "../Api";

export default function LoanForm({ onSave }) {
  const [books, setBooks] = useState([]);
  const [loans, setLoans] = useState([]);
  const [selectedBook, setSelectedBook] = useState("");
  const [message, setMessage] = useState("");

  const isSuperUser = JSON.parse(localStorage.getItem("user_info"))?.is_superuser;

  useEffect(() => {
    if (isSuperUser) {
      const fetchData = async () => {
        try {
          const booksData = await getBooks();
          const loansData = await getLoans();
          setBooks(Array.isArray(booksData) ? booksData : booksData.results || []);
          setLoans(Array.isArray(loansData) ? loansData : loansData.results || []);
        } catch (error) {
          console.error("Erro ao carregar dados:", error.message);
        }
      };
      fetchData();
    }
  }, [isSuperUser]);

  if (!isSuperUser) {
    return <p className="access-denied">‚ö†Ô∏è Acesso negado. Apenas superusu√°rios podem gerenciar empr√©stimos.</p>;
  }

  const handleLoan = async (e) => {
    e.preventDefault();
    if (!selectedBook) {
      setMessage("‚ö†Ô∏è Selecione um livro.");
      return;
    }
    try {
      await createLoan({ book: selectedBook });
      setMessage("‚úÖ Empr√©stimo registrado!");
      setSelectedBook("");
      onSave?.();
    } catch (error) {
      setMessage(`‚ùå Erro: ${error.message}`);
    }
  };

  const handleReturn = async (loanId) => {
    try {
      await returnBook({ loan_id: loanId });
      setMessage("‚úÖ Devolu√ß√£o registrada!");
      onSave?.();
    } catch (error) {
      setMessage(`‚ùå Erro: ${error.message}`);
    }
  };

  return (
    <div className="loan-form">
      <h3>üìö Gerenciar Empr√©stimos</h3>
      {message && <p>{message}</p>}
      <form onSubmit={handleLoan}>
        <select value={selectedBook} onChange={(e) => setSelectedBook(e.target.value)}>
          <option value="">-- Selecione um livro --</option>
          {Array.isArray(books) &&
            books.map((b) => (
              <option key={b.id} value={b.id}>
                {b.title}
              </option>
            ))}
        </select>
        <button type="submit">Emprestar</button>
      </form>

      <h4>Empr√©stimos Ativos</h4>
      <ul>
        {Array.isArray(loans) &&
          loans.filter((l) => !l.returned).map((l) => (
            <li key={l.id}>
              {l.book?.title} ‚Äî {l.user?.username}
              <button onClick={() => handleReturn(l.id)}>Devolver</button>
            </li>
          ))}
      </ul>
    </div>
  );
}
