import React, { useState, useRef } from "react";
import axios from "axios";
import "../assets/styles.css";

export default function LoginForm({ setToken, setUser, setRole }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const errorRef = useRef(null);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      // üîπ Login JWT (obten√ß√£o do token)
      const response = await axios.post("http://127.0.0.1:8000/api/token/", {
        username,
        password,
      });

      const accessToken = response.data.access;
      localStorage.setItem("access_token", accessToken);

      if (setToken) setToken(accessToken);

      // üîπ Buscar dados do usu√°rio logado
      const userRes = await axios.get("http://127.0.0.1:8000/api/users/me/", {
        headers: { Authorization: `Bearer ${accessToken}` },
      });

      const userData = userRes.data;
      if (setUser) setUser(userData);

      // üîπ Definir fun√ß√£o (role)
      if (userData.is_superuser) setRole("superuser");
      else if (userData.is_staff) setRole("admin");
      else setRole("user");

      // Limpar campos
      setUsername("");
      setPassword("");
    } catch (err) {
      console.error("Erro no login:", err);
      setError("‚ùå Credenciais inv√°lidas. Verifique usu√°rio e senha.");
      // Foco autom√°tico na mensagem para leitores de tela
      setTimeout(() => errorRef.current?.focus(), 100);
    } finally {
      setLoading(false);
    }
  };

  return (
    <section
      className="login-section"
      aria-label="√Årea de login do sistema"
      style={{
        maxWidth: "400px",
        margin: "3rem auto",
        background: "#fff",
        padding: "2rem",
        borderRadius: "10px",
        boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
      }}
    >
      <h2 tabIndex="0" style={{ textAlign: "center", color: "#003366" }}>
        üîê Login do Sistema
      </h2>

      {error && (
        <div
          role="alert"
          ref={errorRef}
          tabIndex="-1"
          className="error-msg"
          aria-live="assertive"
          style={{
            background: "#fdd",
            color: "#900",
            padding: "0.8rem",
            margin: "1rem 0",
            borderRadius: "6px",
            textAlign: "center",
            fontWeight: "bold",
          }}
        >
          {error}
        </div>
      )}

      <form
        onSubmit={handleLogin}
        aria-label="Formul√°rio de login"
        style={{ display: "flex", flexDirection: "column", gap: "1rem" }}
      >
        <label htmlFor="username" className="font-semibold">
          Usu√°rio:
        </label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          autoComplete="username"
          aria-required="true"
        />

        <label htmlFor="password" className="font-semibold">
          Senha:
        </label>
        <input
          id="password"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          autoComplete="current-password"
          aria-required="true"
        />

        <button
          type="submit"
          disabled={loading}
          aria-label="Entrar no sistema"
          style={{
            padding: "0.75rem",
            background: loading ? "#999" : "#004999",
            color: "white",
            fontWeight: "bold",
            border: "none",
            borderRadius: "6px",
            cursor: loading ? "not-allowed" : "pointer",
            transition: "background 0.3s",
          }}
        >
          {loading ? "Entrando..." : "Entrar"}
        </button>
      </form>
    </section>
  );
}
