import React, { useState } from "react";
import "../App.css"; // mant√©m estilos globais
import "./BookForm.css"; // ‚¨ÖÔ∏è nome correto com B e F mai√∫sculos
import { saveBook } from "../Api"; // integra√ß√£o com sua API

export default function BookForm({ onBookAdded }) {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [category, setCategory] = useState("");
  const [description, setDescription] = useState("");
  const [message, setMessage] = useState("");

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      const newBook = { title, author, category, description };
      await saveBook(newBook);
      setMessage("‚úÖ Livro salvo com sucesso!");
      setTitle("");
      setAuthor("");
      setCategory("");
      setDescription("");
      if (onBookAdded) onBookAdded(); // atualiza lista sem recarregar
    } catch (error) {
      console.error("Erro ao salvar livro:", error);
      setMessage("‚ùå Erro ao salvar o livro.");
    }
  }

  return (
    <form className="book-form" onSubmit={handleSubmit}>
      <h3>üìö Cadastrar Novo Livro</h3>

      <label htmlFor="title">T√≠tulo:</label>
      <input
        id="title"
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
        aria-required="true"
      />

      <label htmlFor="author">Autor:</label>
      <input
        id="author"
        type="text"
        value={author}
        onChange={(e) => setAuthor(e.target.value)}
        required
      />

      <label htmlFor="category">Categoria:</label>
      <input
        id="category"
        type="text"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      />

      <label htmlFor="description">Descri√ß√£o:</label>
      <textarea
        id="description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        rows="3"
      ></textarea>

      <button type="submit">Salvar Livro</button>

      {message && (
        <p
          className={`form-message ${
            message.includes("sucesso") ? "success" : "error"
          }`}
        >
          {message}
        </p>
      )}
    </form>
  );
}
